{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Counts of papers classified by the regex rule and distribution of empty key fields (Title, Abstract, AuthorKeywords). This helps estimate potential false negatives due to missing information and to assess rule robustness.",
  "data": { "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv" },
  "transform": [
    {
      "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, datum.AuthorKeywords || '')",
      "as": "kw_authorkeywords"
    },
    {
      "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, datum.Abstract || '')",
      "as": "kw_abstract"
    },
    {
      "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, datum.Title || '')",
      "as": "kw_title"
    },
    {
      "calculate": "(datum.kw_authorkeywords ? 1 : 0) + (datum.kw_abstract ? 1 : 0) + (datum.kw_title ? 1 : 0)",
      "as": "num_fields_matched"
    },
    {
      "calculate": "datum.num_fields_matched > 0 ? 'AutoVis' : 'Other'",
      "as": "Label"
    },
    {
      "calculate": "((datum.AuthorKeywords || '') == '' ? 1 : 0) + ((datum.Abstract || '') == '' ? 1 : 0) + ((datum.Title || '') == '' ? 1 : 0)",
      "as": "num_empty_fields"
    }
  ],
  "mark": "bar",
  "encoding": {
    "x": { "field": "Label", "type": "nominal", "axis": { "title": "AutoVis classification" } },
    "y": { "aggregate": "count", "type": "quantitative", "axis": { "title": "Number of papers" } },
    "color": { "field": "num_empty_fields", "type": "ordinal", "title": "# empty fields (Title, Abstract, AuthorKeywords)" },
    "tooltip": [
      { "field": "Label", "type": "nominal" },
      { "field": "num_empty_fields", "type": "quantitative", "title": "empty fields" },
      { "aggregate": "count", "type": "quantitative", "title": "papers" }
    ]
  },
  "title": "AutoVis detection: counts by label and missing fields (helps identify potential false negatives)"
}

