{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Counts per year for AutoVis vs Other and the yearly fraction of AutoVis papers.",
  "data": { "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv" },
  "transform": [
    {
      "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, (datum.AuthorKeywords || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.Title || '')) ? 'AutoVis' : 'Other'",
      "as": "Category"
    },
    {
      "aggregate": [ { "op": "count", "as": "Count" } ],
      "groupby": [ "Year", "Category" ]
    },
    {
      "joinaggregate": [ { "op": "sum", "field": "Count", "as": "TotalInYear" } ],
      "groupby": [ "Year" ]
    },
    {
      "calculate": "datum.Count / datum.TotalInYear",
      "as": "Proportion"
    }
  ],
  "width": 700,
  "height": 360,
  "layer": [
    {
      "mark": { "type": "bar", "cornerRadiusTopLeft": 2, "cornerRadiusTopRight": 2 },
      "encoding": {
        "x": { "field": "Year", "type": "ordinal", "axis": { "title": "Year", "labelAngle": 0 } },
        "y": { "field": "Count", "type": "quantitative", "axis": { "title": "Number of papers" } },
        "color": { "field": "Category", "type": "nominal", "scale": { "range": ["#1f77b4", "#ff7f0e"] } },
        "xOffset": { "field": "Category" },
        "tooltip": [
          { "field": "Year", "type": "ordinal" },
          { "field": "Category", "type": "nominal" },
          { "field": "Count", "type": "quantitative" },
          { "field": "Proportion", "type": "quantitative", "format": ".1%" }
        ]
      }
    },
    {
      "transform": [ { "filter": "datum.Category == 'AutoVis'" } ],
      "mark": { "type": "line", "point": true, "strokeWidth": 2 },
      "encoding": {
        "x": { "field": "Year", "type": "ordinal" },
        "y": { "field": "Proportion", "type": "quantitative", "axis": { "title": "Proportion of AutoVis (right)", "format": ".0%", "orient": "right" } },
        "color": { "value": "#000000" },
        "tooltip": [
          { "field": "Year", "type": "ordinal" },
          { "field": "Proportion", "type": "quantitative", "format": ".1%" }
        ]
      }
    }
  ],
  "resolve": { "scale": { "y": "independent" } }
}

{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "data": {"url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv"},
  "transform": [
    {
      "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, (datum.AuthorKeywords || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.Title || '')) ? 1 : 0",
      "as": "isAutoVis"
    },
    {
      "aggregate": [
        {"op": "sum", "field": "isAutoVis", "as": "AutoVisCount"},
        {"op": "count", "as": "TotalPapers"}
      ],
      "groupby": ["Conference", "Year"]
    },
    {"calculate": "datum.AutoVisCount / datum.TotalPapers", "as": "Proportion_of_AutoVis"},
    {"filter": "datum.TotalPapers >= 5"}
  ],
  "mark": {"type": "line", "point": true},
  "encoding": {
    "x": {"field": "Year", "type": "quantitative", "title": "Year", "axis": {"format": "d", "labelAngle": 0}},
    "y": {"field": "Proportion_of_AutoVis", "type": "quantitative", "title": "Proportion of AutoVis papers", "axis": {"format": ".0%"}},
    "color": {"field": "Conference", "type": "nominal", "title": "Conference"},
    "tooltip": [
      {"field": "Conference", "type": "nominal"},
      {"field": "Year", "type": "quantitative"},
      {"field": "AutoVisCount", "type": "quantitative", "title": "AutoVis count"},
      {"field": "TotalPapers", "type": "quantitative", "title": "Total papers"},
      {"field": "Proportion_of_AutoVis", "type": "quantitative", "title": "Proportion", "format": ".1%"}
    ]
  },
  "width": 800,
  "height": 400,
  "title": "Proportion of AutoVis papers per conference-year (conference-years with >= 5 papers)"
}

{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Year-level trend of paper impact (median ± IQR) for AutoVis vs Other across three candidate impact metrics. Point size encodes number of papers in that year-group; low-sample years are drawn with reduced opacity (count < 5). Tooltip shows count, median, mean and IQR.",
  "data": {
    "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv"
  },
  "transform": [
    {
      "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, (datum.AuthorKeywords || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.Title || '')) ? 'AutoVis' : 'Other'",
      "as": "Group"
    },
    {
      "fold": ["AminerCitationCount", "CitationCount_CrossRef", "Downloads_Xplore"],
      "as": ["metric", "value"]
    },
    {
      "filter": "datum.value !== null && isFinite(+datum.value)"
    },
    {
      "calculate": "(datum.Year === null || datum.Year === '') ? null : parseInt(datum.Year)",
      "as": "YearNum"
    },
    {
      "aggregate": [
        {"op": "count", "field": "value", "as": "count"},
        {"op": "median", "field": "value", "as": "median"},
        {"op": "q1", "field": "value", "as": "q1"},
        {"op": "q3", "field": "value", "as": "q3"},
        {"op": "mean", "field": "value", "as": "mean"}
      ],
      "groupby": ["YearNum", "Group", "metric"]
    }
  ],
  "width": 760,
  "height": 160,
  "resolve": { "scale": { "color": "independent" } },
  "facet": {
    "row": {
      "field": "metric",
      "type": "nominal",
      "header": {"labelAngle": 0, "labelFontWeight": "bold"}
    }
  },
  "spec": {
    "layer": [
      {
        "mark": { "type": "rule", "cornerRadius": 1 },
        "encoding": {
          "x": {"field": "YearNum", "type": "ordinal", "axis": {"title": "Year"}},
          "y": {"field": "q1", "type": "quantitative", "title": "Impact"},
          "y2": {"field": "q3"},
          "color": {"field": "Group", "type": "nominal"},
          "opacity": {"condition": {"test": "datum.count >= 5", "value": 0.9}, "value": 0.22},
          "tooltip": [
            {"field": "metric", "type": "nominal", "title": "Metric"},
            {"field": "YearNum", "type": "ordinal", "title": "Year"},
            {"field": "Group", "type": "nominal", "title": "Group"},
            {"field": "count", "type": "quantitative", "title": "Papers (count)"},
            {"field": "median", "type": "quantitative", "title": "Median"},
            {"field": "mean", "type": "quantitative", "title": "Mean"},
            {"field": "q1", "type": "quantitative", "title": "Q1 (25%)"},
            {"field": "q3", "type": "quantitative", "title": "Q3 (75%)"}
          ]
        }
      },
      {
        "mark": { "type": "line", "point": false, "interpolate": "monotone" },
        "encoding": {
          "x": {"field": "YearNum", "type": "ordinal"},
          "y": {"field": "median", "type": "quantitative"},
          "color": {"field": "Group", "type": "nominal"},
          "opacity": {"condition": {"test": "datum.count >= 5", "value": 1}, "value": 0.25}
        }
      },
      {
        "mark": { "type": "point", "filled": true },
        "encoding": {
          "x": {"field": "YearNum", "type": "ordinal"},
          "y": {"field": "median", "type": "quantitative"},
          "color": {"field": "Group", "type": "nominal"},
          "size": {"field": "count", "type": "quantitative", "scale": {"range": [20, 200]}, "title": "Papers (count)"},
          "opacity": {"condition": {"test": "datum.count >= 5", "value": 1}, "value": 0.28},
          "tooltip": [
            {"field": "metric", "type": "nominal", "title": "Metric"},
            {"field": "YearNum", "type": "ordinal", "title": "Year"},
            {"field": "Group", "type": "nominal", "title": "Group"},
            {"field": "count", "type": "quantitative", "title": "Papers (count)"},
            {"field": "median", "type": "quantitative", "title": "Median"},
            {"field": "mean", "type": "quantitative", "title": "Mean"},
            {"field": "q1", "type": "quantitative", "title": "Q1 (25%)"},
            {"field": "q3", "type": "quantitative", "title": "Q3 (75%)"}
          ]
        }
      }
    ]
  }
}

