{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "data": {"url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv"},
  "vconcat": [
    {
      "title": "AutoVis paper counts by year (yearly bins) with 3-year rolling average\nRecommended: yearly bin with 3-year rolling average",
      "transform": [
        {
          "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, (datum.AuthorKeywords || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.Title || '')) ? 'AutoVis' : 'Other'",
          "as": "Category"
        },
        {"filter": "datum.Year != null"},
        {"aggregate": [{"op": "count", "as": "count"}], "groupby": ["Year", "Category"]},
        {"window": [{"op": "mean", "field": "count", "as": "rolling_mean"}], "groupby": ["Category"], "sort": [{"field": "Year", "order": "ascending"}], "frame": [-2, 0]}
      ],
      "layer": [
        {
          "mark": {"type": "line", "point": false},
          "encoding": {
            "x": {"field": "Year", "type": "temporal", "timeUnit": "year", "title": "Year"},
            "y": {"field": "count", "type": "quantitative", "title": "Paper count"},
            "color": {"field": "Category", "type": "nominal"}
          }
        },
        {
          "mark": {"type": "line", "strokeDash": [4, 4], "opacity": 0.9},
          "encoding": {
            "x": {"field": "Year", "type": "temporal", "timeUnit": "year"},
            "y": {"field": "rolling_mean", "type": "quantitative", "title": "3-year rolling mean"},
            "color": {"field": "Category", "type": "nominal"}
          }
        },
        {
          "mark": {"type": "point"},
          "encoding": {
            "x": {"field": "Year", "type": "temporal", "timeUnit": "year"},
