{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Top AutoVis papers by combined impact (normalized citations + downloads). Filters papers by keyword matches in Title/Abstract/AuthorKeywords.",
  "data": {
    "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv",
    "format": { "type": "csv" }
  },
  "transform": [
    {
      "calculate": "(datum.AuthorKeywords || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.Title || '')",
      "as": "combined_text"
    },
    {
      "filter": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, datum.combined_text)"
    },
    {
      "calculate": "isNaN(toNumber(datum.CitationCount_CrossRef)) ? 0 : toNumber(datum.CitationCount_CrossRef)",
      "as": "CitationCount_CrossRef_num"
    },
    {
      "calculate": "isNaN(toNumber(datum.AminerCitationCount)) ? 0 : toNumber(datum.AminerCitationCount)",
      "as": "AminerCitationCount_num"
    },
    {
      "calculate": "isNaN(toNumber(datum.Downloads_Xplore)) ? 0 : toNumber(datum.Downloads_Xplore)",
      "as": "Downloads_Xplore_num"
    },
    {
      "joinaggregate": [
        { "op": "min", "field": "CitationCount_CrossRef_num", "as": "min_CitationCount_CrossRef" },
        { "op": "max", "field": "CitationCount_CrossRef_num", "as": "max_CitationCount_CrossRef" },
        { "op": "min", "field": "AminerCitationCount_num", "as": "min_AminerCitationCount" },
        { "op": "max", "field": "AminerCitationCount_num", "as": "max_AminerCitationCount" },
        { "op": "min", "field": "Downloads_Xplore_num", "as": "min_Downloads_Xplore" },
        { "op": "max", "field": "Downloads_Xplore_num", "as": "max_Downloads_Xplore" }
      ]
    },
    {
      "calculate": "(datum.max_CitationCount_CrossRef == datum.min_CitationCount_CrossRef) ? 0 : (datum.CitationCount_CrossRef_num - datum.min_CitationCount_CrossRef) / (datum.max_CitationCount_CrossRef - datum.min_CitationCount_CrossRef)",
      "as": "norm_citation_crossref"
    },
    {
      "calculate": "(datum.max_AminerCitationCount == datum.min_AminerCitationCount) ? 0 : (datum.AminerCitationCount_num - datum.min_AminerCitationCount) / (datum.max_AminerCitationCount - datum.min_AminerCitationCount)",
      "as": "norm_aminer"
    },
    {
      "calculate": "(datum.max_Downloads_Xplore == datum.min_Downloads_Xplore) ? 0 : (datum.Downloads_Xplore_num - datum.min_Downloads_Xplore) / (datum.max_Downloads_Xplore - datum.min_Downloads_Xplore)",
      "as": "norm_downloads"
    },
    {
      "calculate": "(datum.norm_citation_crossref + datum.norm_aminer + datum.norm_downloads) / 3",
      "as": "combined_impact_score"
    },
    {
      "window": [{ "op": "rank", "as": "rank" }],
      "sort": [{ "field": "combined_impact_score", "order": "descending" }]
    },
    {
      "filter": "datum.rank <= 15"
    }
  ],
  "height": { "step": 28 },
  "mark": "bar",
  "encoding": {
    "y": {
      "field": "Title",
      "type": "nominal",
      "sort": { "field": "combined_impact_score", "order": "descending" },
      "axis": { "labelLimit": 400, "title": "Paper Title" }
    },
    "x": {
      "field": "combined_impact_score",
      "type": "quantitative",
      "axis": { "title": "Combined impact (normalized 0–1)", "tickCount": 5 }
    },
    "color": {
      "field": "Year",
      "type": "ordinal",
      "legend": { "title": "Year" }
    },
    "tooltip": [
      { "field": "Title", "type": "nominal", "title": "Title" },
