{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Author-level aggregates (publication_count vs total_citations). Citations: prefer AminerCitationCount with fallback to CitationCount_CrossRef. Coauthorships approximated as sum of (authors_per_paper - 1) across papers. Top 30 authors by publication count are shown; top 10 annotated.",
  "data": {
    "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv",
    "format": { "type": "csv" }
  },
  "transform": [
    {
      "calculate": "split((datum['AuthorNames-Deduped'] || datum['AuthorNames'] || ''), ';')",
      "as": "authors_arr"
    },
    {
      "calculate": "toNumber(datum['AminerCitationCount']) || toNumber(datum['CitationCount_CrossRef']) || 0",
      "as": "citations"
    },
    { "flatten": ["authors_arr"], "as": ["author"] },
    {
      "calculate": "trim(datum.author)",
      "as": "author"
    },
    {
      "filter": "datum.author != null && datum.author !== ''"
    },
    {
      "calculate": "(datum.authors_arr ? datum.authors_arr.length : 1) - 1",
      "as": "coauthors"
    },
    {
      "aggregate": [
        { "op": "count", "as": "publication_count" },
        { "op": "sum", "field": "citations", "as": "total_citations" },
        { "op": "mean", "field": "citations", "as": "mean_citations" },
        { "op": "sum", "field": "coauthors", "as": "coauthorships" }
      ],
      "groupby": ["author"]
    },
    { "filter": "datum.publication_count > 0" },
    {
      "window": [ { "op": "row_number", "as": "rank" } ],
      "sort": [ { "field": "publication_count", "order": "descending" } ]
    },
    { "filter": "datum.rank <= 30" }
  ],
  "layer": [
    {
      "mark": { "type": "point", "filled": true },
      "encoding": {
        "x": {
          "field": "publication_count",
          "type": "quantitative",
          "title": "Publication count"
        },
        "y": {
          "field": "total_citations",
          "type": "quantitative",
          "title": "Total citations"
        },
        "size": {
          "field": "mean_citations",
          "type": "quantitative",
          "title": "Mean citations (per paper)",
          "scale": { "range": [40, 600] }
        },
        "color": {
          "field": "coauthorships",
          "type": "quantitative",
          "title": "Coauthorships (sum per author, proxy centrality)",
          "scale": { "scheme": "viridis" }
        },
        "opacity": { "value": 0.85 },
        "tooltip": [
          { "field": "author", "type": "nominal", "title": "Author" },
          { "field": "publication_count", "type": "quantitative", "title": "Publications" },
          { "field": "total_citations", "type": "quantitative", "title": "Total citations" },
          { "field": "mean_citations", "type": "quantitative", "title": "Mean citations" },
          { "field": "coauthorships", "type": "quantitative", "title": "Coauthorships (sum)" },
          { "field": "rank", "type": "ordinal", "title": "Rank by publications" }
        ]
      }
    },
    {
      "transform": [ { "filter": "datum.rank <= 10" } ],
      "mark": { "type": "text", "align": "left", "dx": 7, "dy": -7, "fontWeight": "bold" },
      "encoding": {
        "x": { "field": "publication_count", "type": "quantitative" },
        "y": { "field": "total_citations", "type": "quantitative" },
        "text": { "field": "author", "type": "nominal" }
