{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Find the top-cited AutoVis paper (using keywords in Title/Abstract/AuthorKeywords). Shows the top 1 AutoVis paper by CrossRef citation count with tooltip details.",
  "data": {
    "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv",
    "format": { "type": "csv" }
  },
  "transform": [
    {
      "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, (datum.AuthorKeywords || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.Title || '')) ? 'AutoVis' : 'Other'",
      "as": "AutoVis"
    },
    {
      "calculate": "toNumber(datum.CitationCount_CrossRef)",
      "as": "CitationCount"
    },
    { "filter": "datum.AutoVis === 'AutoVis'" },
    {
      "window": [{ "op": "rank", "as": "rank" }],
      "sort": [{ "field": "CitationCount", "order": "descending" }]
    },
    { "filter": "datum.rank <= 1" }
  ],
  "mark": { "type": "bar", "tooltip": true },
  "encoding": {
    "y": { "field": "Title", "type": "nominal", "axis": { "title": "Title" } },
    "x": { "field": "CitationCount", "type": "quantitative", "axis": { "title": "CitationCount_CrossRef" } },
    "color": { "field": "Conference", "type": "nominal", "legend": { "title": "Conference" } },
    "tooltip": [
      { "field": "Title", "type": "nominal", "title": "Title" },
      { "field": "AuthorNames", "type": "nominal", "title": "Authors" },
      { "field": "Year", "type": "ordinal", "title": "Year" },
      { "field": "Conference", "type": "nominal", "title": "Conference" },
      { "field": "CitationCount", "type": "quantitative", "title": "CitationCount_CrossRef" }
    ]
  },
  "width": 640,
  "height": 120,
  "title": "Top AutoVis paper by CrossRef citations (top 1)"
}

