{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Counts of papers at each pipeline step (raw, dedup, time-window) with AutoVis vs Other breakdown. Use the start/end year controls to change the time-window filter.",
  "params": [
    {
      "name": "startYear",
      "value": 2000,
      "bind": { "input": "number", "min": 1900, "max": 2030, "step": 1 }
    },
    {
      "name": "endYear",
      "value": 2020,
      "bind": { "input": "number", "min": 1900, "max": 2030, "step": 1 }
    }
  ],
  "hconcat": [
    {
      "title": "1) Raw dataset",
      "width": 200,
      "height": 240,
      "data": { "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv" },
      "transform": [
        {
          "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, (datum['AuthorKeywords'] || '') + ' ' + (datum['Abstract'] || '') + ' ' + (datum['Title'] || '')) ? 'AutoVis' : 'Other'",
          "as": "AutoVis"
        },
        {
          "aggregate": [{ "op": "count", "as": "count" }],
          "groupby": ["AutoVis"]
        }
      ],
      "mark": "bar",
      "encoding": {
        "x": { "field": "AutoVis", "type": "nominal", "axis": { "labelAngle": 0 } },
        "y": { "field": "count", "type": "quantitative", "title": "Number of papers" },
        "color": { "field": "AutoVis", "type": "nominal", "legend": null },
        "tooltip": [
          { "field": "AutoVis", "type": "nominal" },
          { "field": "count", "type": "quantitative" }
        ]
      }
    },
    {
      "title": "2) After de-duplication (by DOI/Title)",
      "width": 200,
      "height": 240,
      "data": { "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv" },
      "transform": [
        {
          "calculate": "(datum.DOI || '') + '|' + (datum.Title || '')",
          "as": "_key"
        },
        {
          "window": [{ "op": "row_number", "as": "rn" }],
          "groupby": ["_key"],
          "sort": [{ "field": "Year", "order": "descending" }]
        },
        { "filter": "datum.rn === 1" },
        {
          "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, (datum['AuthorKeywords'] || '') + ' ' + (datum['Abstract'] || '') + ' ' + (datum['Title'] || '')) ? 'AutoVis' : 'Other'",
          "as": "AutoVis"
        },
        {
          "aggregate": [{ "op": "count", "as": "count" }],
          "groupby": ["AutoVis"]
        }
      ],
      "mark": "bar",
      "encoding": {
        "x": { "field": "AutoVis", "type": "nominal", "axis": { "labelAngle": 0 } },
        "y": { "field": "count", "type": "quantitative", "title": "Number of papers" },
        "color": { "field": "AutoVis", "type": "nominal", "legend": null },
        "tooltip": [
          { "field": "AutoVis", "type": "nominal" },
          { "field": "count", "type": "quantitative" }
        ]
      }
    },
    {
      "title": "3) After time-window filter (deduped + year range)",
      "width": 200,
      "height": 240,
      "data": { "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv" },
      "transform": [
        {
          "calculate": "(datum.DOI || '') + '|' + (datum.Title || '')",
          "as": "_key"
        },
        {
          "window": [{ "op": "row_number", "as": "rn" }],
          "groupby": ["_key"],
          "sort": [{ "field": "Year", "order": "descending" }]
        },
        { "filter": "datum.rn === 1" },
        { "filter": "datum.Year >= startYear && datum.Year <= endYear" },
        {
          "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, (datum['AuthorKeywords'] || '') + ' ' + (datum['Abstract'] || '') + ' ' + (datum['Title'] || '')) ? 'AutoVis' : 'Other'",
          "as": "AutoVis"
        },
        {
          "aggregate": [{ "op": "count", "as": "count" }],
          "groupby": ["AutoVis"]
        }
      ],
      "mark": "bar",
      "encoding": {
        "x": { "field": "AutoVis", "type": "nominal", "axis": { "labelAngle": 0 } },
        "y": { "field": "count", "type": "quantitative", "title": "Number of papers" },
        "color": { "field": "AutoVis", "type": "nominal", "legend": null },
        "tooltip": [
          { "field": "AutoVis", "type": "nominal" },
          { "field": "count", "type": "quantitative" }
        ]
      }
    },
    {
      "title": "4) After keyword labelling (deduped + time-window; labelled AutoVis vs Other)",
      "width": 200,
      "height": 240,
      "data": { "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv" },
      "transform": [
        {
          "calculate": "(datum.DOI || '') + '|' + (datum.Title || '')",
          "as": "_key"
        },
        {
          "window": [{ "op": "row_number", "as": "rn" }],
          "groupby": ["_key"],
          "sort": [{ "field": "Year", "order": "descending" }]
        },
        { "filter": "datum.rn === 1" },
        { "filter": "datum.Year >= startYear && datum.Year <= endYear" },
        {
          "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, (datum['AuthorKeywords'] || '') + ' ' + (datum['Abstract'] || '') + ' ' + (datum['Title'] || '')) ? 'AutoVis' : 'Other'",
          "as": "AutoVis"
        },
        {
          "aggregate": [{ "op": "count", "as": "count" }],
          "groupby": ["AutoVis"]
        }
      ],
      "mark": "bar",
      "encoding": {
        "x": { "field": "AutoVis", "type": "nominal", "axis": { "labelAngle": 0 } },
        "y": { "field": "count", "type": "quantitative", "title": "Number of papers" },
        "color": { "field": "AutoVis", "type": "nominal", "legend": null },
        "tooltip": [
          { "field": "AutoVis", "type": "nominal" },
          { "field": "count", "type": "quantitative" }
        ]
      }
    }
  ],
  "config": { "view": { "continuousHeight": 240 } }
}

