      "y": {"field": "smoothed", "type": "quantitative", "title": "Metric value (3-year centered mean)"},
      "color": {"field": "Category", "type": "nominal", "scale": {"scheme": "category10"}, "title": "Category"},
      "strokeDash": {"field": "Stat", "type": "nominal", "scale": {"domain": ["mean", "median"], "range": [[], [4,2]]}, "title": "Statistic"},
      "tooltip": [
        {"field": "Year", "type": "ordinal", "title": "Year"},
        {"field": "Category", "type": "nominal", "title": "Category"},
        {"field": "Metric", "type": "nominal", "title": "Metric"},
