    "legend": {"labelFontSize": 11, "titleFontSize": 12}
  }
}

{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Rolling-window birth/death counts of 'AutoVis' topics. Topics are derived preferentially from AuthorKeywords-Deduped (fallback to AuthorKeywords). Papers are filtered to those matching AutoVis-related terms. Windows are non-overlapping fixed-size bins (window size = 3 years). Birth = topic's first window; Death = topic's last window. Suggested parameters (editable in transform calculates): windowSize=3, minPapers=2, persistence=2.",
  "data": {
    "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv",
    "format": { "type": "csv" }
  },
  "transform": [
    { "calculate": "datum['AuthorKeywords-Deduped'] || datum.AuthorKeywords || ''", "as": "rawKeywords" },
    { "calculate": "lower(datum.rawKeywords)", "as": "keywords_lc" },
    { "calculate": "replace(datum.keywords_lc, /[,;]+/g, ';')", "as": "keywords_norm" },
    { "calculate": "split(datum.keywords_norm, ';')", "as": "topic_tokens" },
    { "flatten": ["topic_tokens"], "as": "topic_raw" },
    { "calculate": "trim(topic_raw)", "as": "topic" },
    { "filter": "datum.topic !== null && datum.topic !== ''" },

    { "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, (datum['AuthorKeywords'] || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.Title || '')) ? 'AutoVis' : 'Other'", "as": "paperClass" },
    { "filter": "datum.paperClass === 'AutoVis'" },

    { "joinaggregate": [
        { "op": "min", "field": "Year", "as": "minYear" },
        { "op": "max", "field": "Year", "as": "maxYear" }
      ]
    },

    { "calculate": "3", "as": "windowSize" },
    { "calculate": "2", "as": "minPapers" },
    { "calculate": "2", "as": "persistenceWindows" },

    { "calculate": "floor((toNumber(datum.Year) - datum.minYear) / datum.windowSize)", "as": "windowIndex" },
    { "calculate": "datum.minYear + datum.windowIndex * datum.windowSize", "as": "windowStart" },
    { "calculate": "(datum.windowStart) + '-' + (datum.windowStart + datum.windowSize - 1)", "as": "windowLabel" },

    { "joinaggregate": [
        { "op": "min", "field": "windowIndex", "as": "firstWindow" },
        { "op": "max", "field": "windowIndex", "as": "lastWindow" }
      ], "groupby": ["topic"]
    },

    { "aggregate": [ { "op": "count", "as": "papersCount" } ], "groupby": ["topic","windowIndex","windowStart","windowLabel","firstWindow","lastWindow","minYear","maxYear","windowSize","minPapers","persistenceWindows"] },

    { "calculate": "(datum.firstWindow === datum.windowIndex && datum.papersCount >= datum.minPapers) ? 1 : 0", "as": "isBirth" },
    { "calculate": "(datum.lastWindow === datum.windowIndex && datum.papersCount >= datum.minPapers) ? 1 : 0", "as": "isDeath" },

    { "aggregate": [
        { "op": "sum", "field": "isBirth", "as": "births" },
        { "op": "sum", "field": "isDeath", "as": "deaths" },
        { "op": "count", "field": "topic", "as": "topics_present" }
      ],
      "groupby": ["windowIndex","windowStart","windowLabel"]
    },

    { "calculate": "datum.windowStart", "as": "xYear" },
    { "calculate": "toNumber(datum.windowIndex)", "as": "xIndex" }
  ],

  "layer": [
    {
      "mark": { "type": "line", "point": true, "interpolate": "monotone" },
      "encoding": {
        "x": { "field": "xYear", "type": "quantitative", "title": "Window start year (window size = 3 years)", "axis": { "format": "d" } },
        "y": { "field": "births", "type": "quantitative", "title": "Count" },
        "color": { "value": "#1f77b4" },
        "tooltip": [
          { "field": "windowLabel", "type": "nominal", "title": "Window" },
          { "field": "births", "type": "quantitative", "title": "Births" },
          { "field": "deaths", "type": "quantitative", "title": "Deaths" },
          { "field": "topics_present", "type": "quantitative", "title": "Topics present" }
        ]
      }
    },
    {
      "mark": { "type": "line", "point": true, "interpolate": "monotone" },
      "encoding": {
        "x": { "field": "xYear", "type": "quantitative" },
        "y": { "field": "deaths", "type": "quantitative" },
        "color": { "value": "#d62728" }
      }
    },
    {
      "mark": { "type": "bar", "opacity": 0.25 },
      "encoding": {
        "x": { "field": "xYear", "type": "quantitative" },
        "y": { "field": "topics_present", "type": "quantitative" },
        "color": { "value": "#2ca02c" },
        "opacity": { "value": 0.25 }
      }
    }
  ],

  "resolve": { "scale": { "y": "independent" } },

  "title": "AutoVis topic lifecycle (births & deaths) across 3-year windows\n(Topics from AuthorKeywords; windows = fixed 3-year bins; minPapers=2)"
}

{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Top AutoVis authors by productivity (paper count). Bars show number of AutoVis papers per author; color encodes aggregate citations (CitationCount_CrossRef fallback AminerCitationCount). Tooltip includes h-like rank and citation list.",
  "data": {
    "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv"
  },
  "transform": [
    {
      "calculate": "split(datum['AuthorNames-Deduped'] || '', ';')",
      "as": "authors"
    },
    {
      "flatten": ["authors"]
    },
    {
      "calculate": "trim(datum.authors || '')",
      "as": "author"
    },
    {
      "filter": "datum.author != ''"
    },
    {
      "filter": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, (datum.AuthorKeywords || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.Title || ''))"
    },
    {
      "calculate": "(!isNaN(+datum.CitationCount_CrossRef) ? +datum.CitationCount_CrossRef : (!isNaN(+datum.AminerCitationCount) ? +datum.AminerCitationCount : 0))",
      "as": "citations"
    },
    {
      "aggregate": [
        { "op": "count", "as": "paper_count" },
        { "op": "values", "field": "citations", "as": "citations_list" },
        { "op": "sum", "field": "citations", "as": "total_citations" }
      ],
      "groupby": ["author"]
    },
    {
      "calculate": "(function(arr){arr=(arr||[]).map(function(x){return +x}).sort(function(a,b){return b-a}); for(var i=0;i<arr.length;i++){ if(arr[i] < i+1) return i;} return arr.length})(datum.citations_list)",
      "as": "h_like"
    },
    {
      "window": [
        {
          "op": "rank",
          "as": "rank_papers"
        }
      ],
      "sort": [
        {
          "field": "paper_count",
          "order": "descending"
        }
      ]
    },
    {
      "filter": "datum.rank_papers <= 20"
    }
  ],
  "width": 800,
  "height": 600,
  "mark": {
    "type": "bar",
    "tooltip": true
  },
  "encoding": {
    "x": {
      "field": "paper_count",
      "type": "quantitative",
      "title": "Number of AutoVis papers (productivity)"
    },
    "y": {
      "field": "author",
      "type": "nominal",
      "title": "Author",
      "sort": {
        "field": "paper_count",
        "op": "max",
        "order": "descending"
      }
    },
    "color": {
      "field": "total_citations",
      "type": "quantitative",
      "title": "Aggregate citations",
      "scale": { "scheme": "blues" }
    },
    "tooltip": [
      { "field": "author", "type": "nominal", "title": "Author" },
      { "field": "paper_count", "type": "quantitative", "title": "Paper count" },
      { "field": "total_citations", "type": "quantitative", "title": "Total citations" },
      { "field": "h_like", "type": "quantitative", "title": "h-like rank" },
      { "field": "citations_list", "type": "ordinal", "title": "Citations per paper (list)" }
    ]
  },
  "title": "Top AutoVis Authors: Productivity vs Aggregate Citations (top 20 by paper count)"
}

{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Top institutions producing AutoVis papers: paper counts and aggregate citations (heuristic institution extraction from AuthorAffiliation).",
  "data": {
    "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv",
    "format": { "type": "csv" }
  },
  "transform": [
    {
      "filter": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, (datum.AuthorKeywords || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.Title || ''))"
    },
    { "calculate": "split(datum.AuthorAffiliation || '', ';')", "as": "affs" },
    { "flatten": ["affs"] },
    { "calculate": "trim(datum.affs)", "as": "aff_trim" },
    { "calculate": "(datum.aff_trim || '').toLowerCase()", "as": "aff_lower" },
    {
      "calculate": "trim(replace(( (datum.aff_lower && datum.aff_lower.match(/[^,]*(university|institute|college|school|laboratory|lab|center|centre|inc|corp|company)[^,]*/i)) ? datum.aff_lower.match(/[^,]*(university|institute|college|school|laboratory|lab|center|centre|inc|corp|company)[^,]*/i)[0] : (split(datum.aff_lower, ',')[1] || split(datum.aff_lower, ',')[0]) ), /[^a-z0-9 ]+/g, ' '))",
      "as": "institution_canonical"
    },
    { "calculate": "(datum.DOI && datum.DOI != '') ? datum.DOI : datum.Title", "as": "paper_id" },

    /* Collapse to unique (institution, paper) rows: keep max citations per paper */
    {
      "aggregate": [
        { "op": "max", "field": "CitationCount_CrossRef", "as": "citations" }
      ],
      "groupby": ["institution_canonical", "paper_id"]
    },

    /* Now aggregate by institution: distinct paper count and sum of citations (per unique paper) */
    {
      "aggregate": [
        { "op": "distinct", "field": "paper_id", "as": "paper_count" },
        { "op": "sum", "field": "citations", "as": "aggregate_citations" }
      ],
      "groupby": ["institution_canonical"]
    },

    /* Rank institutions by paper_count and keep top 20 */
    { "window": [{ "op": "rank", "as": "rank" }], "sort": [{ "field": "paper_count", "order": "descending" }] },
    { "filter": "datum.rank <= 20" }
  ],

  "height": { "step": 24 },
  "mark": "bar",
  "encoding": {
    "y": {
      "field": "institution_canonical",
      "type": "nominal",
      "title": "Institution",
      "sort": "-x"
    },
    "x": {
      "field": "paper_count",
      "type": "quantitative",
      "title": "AutoVis paper count"
    },
    "color": {
      "field": "aggregate_citations",
      "type": "quantitative",
      "title": "Aggregate citations (CrossRef)",
      "scale": { "scheme": "blues" }
    },
    "tooltip": [
      { "field": "institution_canonical", "type": "nominal", "title": "Institution" },
      { "field": "paper_count", "type": "quantitative", "title": "AutoVis paper count" },
      { "field": "aggregate_citations", "type": "quantitative", "title": "Aggregate citations" }
    ]
  },

  "title": "Top institutions for AutoVis papers — paper count (bar) colored by aggregate citations"
}

{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Exploratory multi-panel view to support choosing thresholds and identifying central authors for AutoVis co-authorship networks. Left: cumulative authors remaining if we keep authors with >= min papers (use this to pick min_papers_per_author to reach ~200 nodes). Right-top: scatter of per-author paper_count vs coauthor_sum (proxy degree) for sizing/coloring decisions. Bottom: top-10 authors by coauthor_sum as suggested gatekeepers / ego seeds.",
  "data": { "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv" },
  "vconcat": [
    {
      "title": "Cumulative number of authors vs min_papers_per_author (choose threshold to keep ~200 nodes)",
      "width": 700,
      "height": 220,
      "transform": [
        {
          "filter": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, (datum['AuthorKeywords'] || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.Title || ''))"
        },
        {
          "calculate": "max(0, length(split(datum['AuthorNames-Deduped'] || '', ';')) - 1)",
          "as": "coauthors"
        },
        {
          "calculate": "split(datum['AuthorNames-Deduped'] || '', ';')",
          "as": "authors"
        },
        { "flatten": ["authors"], "as": ["author"] },
        { "calculate": "trim(datum.author)", "as": "author" },
        {
          "aggregate": [
            { "op": "count", "as": "paper_count" },
            { "op": "sum", "field": "coauthors", "as": "coauthor_sum" }
          ],
          "groupby": ["author"]
        },
        {
          "aggregate": [
            { "op": "count", "as": "authors_at_count" }
          ],
          "groupby": ["paper_count"]
        },
        { "sort": [{ "field": "paper_count", "order": "descending" }] },
        {
          "window": [
            {
              "op": "sum",
              "field": "authors_at_count",
              "as": "cumulative_authors"
            }
          ]
        }
      ],
      "layer": [
        {
          "mark": { "type": "area", "interpolate": "step-after", "opacity": 0.25, "color": "#4C78A8" },
          "encoding": {
            "x": { "field": "paper_count", "type": "quantitative", "title": "min_papers_per_author (paper_count)" },
            "y": { "field": "cumulative_authors", "type": "quantitative", "title": "Number of authors with >= threshold" },
            "tooltip": [
              { "field": "paper_count", "type": "quantitative", "title": "paper_count (threshold)" },
              { "field": "authors_at_count", "type": "quantitative", "title": "authors with this paper_count" },
              { "field": "cumulative_authors", "type": "quantitative", "title": "cumulative authors (>=)" }
            ]
          }
        },
        {
          "mark": { "type": "line", "interpolate": "step-after", "color": "#2E8B57" },
