{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Top AuthorKeywords (cleaned) for papers flagged as AutoVis vs Other. This helps show whether AuthorKeywords are informative for extracting coherent AutoVis themes and which keywords are most representative.",
  "data": { "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv" },
  "transform": [
    {
      "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, (datum['AuthorKeywords'] || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.Title || '')) ? 'AutoVis' : 'Other'",
      "as": "AutoVisLabel"
    },
    {
      "calculate": "replace(datum['AuthorKeywords'] || '', /[;]+/g, ',')",
      "as": "kw_joined"
    },
    {
      "calculate": "split(datum.kw_joined, ',')",
      "as": "kwArray"
    },
    { "flatten": ["kwArray"] },
    {
      "calculate": "trim(lower(datum.kwArray))",
      "as": "Keyword"
    },
    {
      "calculate": "replace(datum.Keyword, /[^a-z0-9\s\-]/g, '')",
      "as": "KeywordClean"
    },
    { "filter": "datum.KeywordClean != null && datum.KeywordClean != ''" },
    {
      "aggregate": [{ "op": "count", "as": "count" }],
      "groupby": ["AutoVisLabel", "KeywordClean"]
    },
    {
      "window": [{ "op": "rank", "as": "rank" }],
      "groupby": ["AutoVisLabel"],
      "sort": [{ "field": "count", "order": "descending" }]
    },
    { "filter": "datum.rank <= 12" }
  ],
  "facet": {
    "column": {
      "field": "AutoVisLabel",
      "type": "nominal",
      "header": { "labelFontSize": 12, "titleFontSize": 14 }
    }
  },
  "spec": {
    "width": 320,
    "height": 280,
    "mark": "bar",
    "encoding": {
      "y": {
        "field": "KeywordClean",
        "type": "nominal",
        "axis": { "title": "Keyword (cleaned)", "labelLimit": 300 },
        "sort": { "field": "count", "op": "sum", "order": "descending" }
      },
      "x": { "field": "count", "type": "quantitative", "axis": { "title": "Count" } },
      "color": { "field": "AutoVisLabel", "type": "nominal", "legend": null },
      "tooltip": [
        { "field": "KeywordClean", "type": "nominal", "title": "Keyword" },
        { "field": "count", "type": "quantitative", "title": "Count" },
        { "field": "AutoVisLabel", "type": "nominal", "title": "Label" }
      ]
    }
  },
  "config": { "view": { "stroke": "transparent" }, "axis": { "labelFontSize": 11, "titleFontSize": 12 } }
}

{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Yearly theme prevalence (percent of papers per year) computed by simple keyword-based theme assignment. Use the conference dropdown to filter.",
  "data": { "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv" },
  "params": [
    {
      "name": "Conference_Filter",
      "value": "",
      "bind": {
        "input": "select",
        "options": ["", "InfoVis", "VAST", "Vis", "SciVis", "EuroVis", "TVCG"],
        "labels": ["All conferences", "InfoVis", "VAST", "Vis", "SciVis", "EuroVis", "TVCG"]
      }
    }
  ],
  "transform": [
    {
      "calculate": "(datum.AuthorKeywords || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.Title || '')",
      "as": "_text"
    },
    {
      "calculate": "test(/automated vis|automatic vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, datum._text) ? 'Automated Vis' : test(/set|sets|intersection|intersections|upset/i, datum._text) ? 'Sets' : test(/narrative|storytelling|story|journalism/i, datum._text) ? 'Narrative' : test(/d3|web|javascript|dom|html|css|toolkit|toolkits/i, datum._text) ? 'Web/Tools' : test(/interaction|interactive|user interface|interface|ui/i, datum._text) ? 'Interaction' : test(/evaluation|user study|case study|study|experiment|qualitative|quantitative/i, datum._text) ? 'Evaluation' : 'Other'",
      "as": "Theme"
    },
    {
      "filter": "params.Conference_Filter === '' || datum.Conference === params.Conference_Filter"
    },
    {
      "aggregate": [{ "op": "count", "as": "Count" }],
      "groupby": ["Year", "Theme"]
    },
    {
      "joinaggregate": [{ "op": "sum", "field": "Count", "as": "TotalPerYear" }],
      "groupby": ["Year"]
    },
    {
      "calculate": "datum.TotalPerYear > 0 ? (datum.Count / datum.TotalPerYear) * 100 : 0",
      "as": "Percent"
    },
    {
      "window": [
        { "op": "mean", "field": "Percent", "as": "Percent_smoothed" }
      ],
      "frame": [-2, 2],
      "groupby": ["Theme"],
      "sort": [{ "field": "Year", "order": "ascending" }]
    },
    {
      "calculate": "isFinite(datum.Percent_smoothed) ? datum.Percent_smoothed : datum.Percent",
      "as": "yValue"
    }
  ],
  "mark": { "type": "line", "point": true, "tooltip": true },
  "encoding": {
    "x": {
      "timeUnit": "year",
      "field": "Year",
      "type": "temporal",
      "axis": { "title": "Year" }
    },
    "y": {
      "field": "yValue",
      "type": "quantitative",
      "axis": { "title": "Percent of papers (smoothed %)", "format": ".1f" }
    },
    "color": {
      "field": "Theme",
      "type": "nominal",
      "legend": { "title": "Theme" }
    },
    "opacity": { "value": 0.9 },
    "tooltip": [
      { "field": "Year", "type": "temporal", "timeUnit": "year", "title": "Year" },
      { "field": "Theme", "type": "nominal", "title": "Theme" },
      { "field": "Count", "type": "quantitative", "title": "Count" },
      { "field": "Percent", "type": "quantitative", "format": ".2f", "title": "Percent (%)" },
      { "field": "TotalPerYear", "type": "quantitative", "title": "Total papers (year)" },
      { "field": "Conference", "type": "nominal", "title": "Conference" }
    ]
  },
  "width": 800,
  "height": 420,
  "title": "Theme prevalence over time (per-year percent, 5-year rolling mean)"
}

{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Distribution of themes across venues (conference). Uses the first AuthorKeywords token as a proxy for the paper's dominant theme. For each Venue x Theme we compute counts and percentage within the venue and show faceted bar charts (one facet per venue).",
  "data": {
    "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv"
  },
  "transform": [
    {
      "calculate": "trim(replace(datum.AuthorKeywords || '', /[;,].*/, ''))",
      "as": "Theme"
    },
    {
      "calculate": "(datum.Theme === '') ? 'Other' : datum.Theme",
      "as": "Theme"
    },
    {
      "aggregate": [{ "op": "count", "as": "count" }],
      "groupby": ["Conference", "Theme"]
    },
    {
