{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "AutoVis papers: evaluation-type detection, GraphicsReplicabilityStamp distribution (artifact proxy), and a simple interdisciplinarity heuristic.",
  "data": {
    "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv",
    "format": { "type": "csv" }
  },
  "transform": [
    {
      "filter": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, (datum.AuthorKeywords || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.Title || ''))"
    },
    {
      "calculate": "test(/user study|user-study|field study|lab study|experiment|participant|human subject|usability|user evaluation|usability study/i, (datum.Abstract||'') + ' ' + (datum.Title||'') + ' ' + (datum.AuthorKeywords||'')) ? 'user study' : test(/case study/i, (datum.Abstract||'') + ' ' + (datum.Title||'') + ' ' + (datum.AuthorKeywords||'')) ? 'case study' : test(/benchmark|benchmarking|evaluation framework|evaluation methodology/i, (datum.Abstract||'') + ' ' + (datum.Title||'') + ' ' + (datum.AuthorKeywords||'')) ? 'benchmark' : test(/qualitative|interview|focus group|thematic|ethnography/i, (datum.Abstract||'') + ' ' + (datum.Title||'') + ' ' + (datum.AuthorKeywords||'')) ? 'qualitative' : test(/quantitative|statistic|anova|t-test|p-value|measurement|metric|quantitative/i, (datum.Abstract||'') + ' ' + (datum.Title||'') + ' ' + (datum.AuthorKeywords||'')) ? 'quantitative' : 'other'",
      "as": "EvalType"
    },
    {
      "calculate": "datum.GraphicsReplicabilityStamp && datum.GraphicsReplicabilityStamp !== '' ? datum.GraphicsReplicabilityStamp : 'None'",
      "as": "RepStamp"
    },
    {
      "calculate": "(test(/computer|cs|computer science|dept of computer|department of computer/i, datum.AuthorAffiliation) && test(/physics|psycholog|biolog|medicine|statist|design|sociolog|economics|engineering|biomedical|humanit|chemistry|material|mathematics|cognitive/i, datum.AuthorAffiliation)) ? 'Interdisciplinary' : 'Not Interdisciplinary'",
      "as": "Interdisciplinary"
    }
  ],
  "vconcat": [
    {
      "title": "Detected evaluation types in AutoVis papers (heuristic keyword mapping)",
      "mark": { "type": "bar", "tooltip": true },
      "width": 480,
      "height": 160,
      "encoding": {
        "x": {
          "field": "EvalType",
          "type": "nominal",
          "axis": { "labelAngle": -20, "labelExpr": "datum.label" }
        },
        "y": { "aggregate": "count", "type": "quantitative", "title": "# papers" },
        "color": { "field": "EvalType", "type": "nominal", "legend": null }
      }
    },
    {
      "title": "GraphicsReplicabilityStamp distribution (artifact/reproducibility proxy)",
      "mark": { "type": "bar", "tooltip": true },
      "width": 480,
      "height": 140,
      "encoding": {
        "x": {
          "field": "RepStamp",
          "type": "nominal",
          "axis": { "labelAngle": -20 }
        },
        "y": { "aggregate": "count", "type": "quantitative", "title": "# papers" },
        "color": { "field": "RepStamp", "type": "nominal", "legend": null }
      }
    },
    {
      "title": "Interdisciplinarity heuristic (presence of CS + other domain in affiliations)",
      "mark": { "type": "bar", "tooltip": true },
      "width": 480,
      "height": 120,
      "encoding": {
        "x": { "field": "Interdisciplinary", "type": "nominal" },
        "y": { "aggregate": "count", "type": "quantitative", "title": "# papers" },
        "color": { "field": "Interdisciplinary", "type": "nominal", "legend": null }
      }
    }
  ],
  "config": {
    "view": { "stroke": "transparent" },
    "axis": { "labelFontSize": 11, "titleFontSize": 12 },
    "title": { "fontSize": 13 }
  }
