{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Completeness and distribution of attributes in Vis publications dataset. Bars show non-missing counts per attribute; color indicates completeness percent.",
  "data": {"url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv", "format": {"type": "csv"}},
  "transform": [
    {"calculate": "1", "as": "__one"},
    {"joinaggregate": [{"op": "sum", "field": "__one", "as": "total_records"}]},
    {"fold": ["Conference", "Year", "Title", "DOI", "Link", "FirstPage", "LastPage", "PaperType", "Abstract", "AuthorNames-Deduped", "AuthorNames", "AuthorAffiliation", "InternalReferences", "AuthorKeywords", "AminerCitationCount", "CitationCount_CrossRef", "PubsCited_CrossRef", "Downloads_Xplore", "Award", "GraphicsReplicabilityStamp"], "as": ["attribute", "value"]},
    {"filter": "datum.value !== null && datum.value !== ''"},
    {"aggregate": [{"op": "count", "as": "non_missing_count"}, {"op": "max", "field": "total_records", "as": "total_records"}], "groupby": ["attribute"]},
    {"calculate": "datum.non_missing_count / datum.total_records", "as": "completeness_percent"},
    {"calculate": "format(datum.completeness_percent*100, '.1f') + '%'", "as": "completeness_text"}
  ],
  "mark": {"type": "bar", "tooltip": true},
  "encoding": {
    "y": {"field": "attribute", "type": "nominal", "sort": {"field": "completeness_percent", "order": "descending"}, "axis": {"title": "Attribute"}},
    "x": {"field": "non_missing_count", "type": "quantitative", "axis": {"title": "Non-missing count"}},
    "color": {"field": "completeness_percent", "type": "quantitative", "scale": {"scheme": "blues"}, "legend": {"title": "Completeness (%)", "format": ".0%"}},
    "tooltip": [
      {"field": "attribute", "type": "nominal"},
      {"field": "non_missing_count", "type": "quantitative", "title": "Non-missing count"},
      {"field": "completeness_percent", "type": "quantitative", "format": ".1%", "title": "Completeness"},
      {"field": "total_records", "type": "quantitative", "title": "Total records"}
    ]
  },
  "height": {"step": 20},
  "config": {"axis": {"labelFontSize": 12, "titleFontSize": 13}, "view": {"stroke": "transparent"}}
}

{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Identify 'AutoVis' papers via keyword regex across Title, Abstract, AuthorKeywords; show counts and which fields contributed. Also list up to 5 example AutoVis paper titles.",
  "data": { "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv" },
  "transform": [
    { "calculate": "((datum.Title || '').toLowerCase().trim().replace(/[^a-z0-9 ]/g, ' '))", "as": "procTitle" },
    { "calculate": "((datum.Abstract || '').toLowerCase().trim().replace(/[^a-z0-9 ]/g, ' '))", "as": "procAbstract" },
    { "calculate": "((datum.AuthorKeywords || '').toLowerCase().trim().replace(/[^a-z0-9 ]/g, ' '))", "as": "procKeywords" },
    { "calculate": "datum.procTitle + ' ' + datum.procAbstract + ' ' + datum.procKeywords", "as": "procCombined" },

    { "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/, datum.procTitle)", "as": "titleMatch" },
    { "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/, datum.procAbstract)", "as": "abstractMatch" },
    { "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/, datum.procKeywords)", "as": "keywordsMatch" },

    { "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/, datum.procCombined) ? 'AutoVis' : 'Other'", "as": "match_label" },

    { "calculate": "datum.titleMatch && !datum.abstractMatch && !datum.keywordsMatch ? 'Title-only' : (!datum.titleMatch && datum.abstractMatch && !datum.keywordsMatch ? 'Abstract-only' : (!datum.titleMatch && !datum.abstractMatch && datum.keywordsMatch ? 'Keywords-only' : (datum.titleMatch && datum.abstractMatch && !datum.keywordsMatch ? 'Title & Abstract' : (datum.titleMatch && datum.keywordsMatch && !datum.abstractMatch ? 'Title & Keywords' : (datum.abstractMatch && datum.keywordsMatch && !datum.titleMatch ? 'Abstract & Keywords' : (datum.titleMatch && datum.abstractMatch && datum.keywordsMatch ? 'All fields' : 'None'))))))", "as": "match_field" }
  ],

  "vconcat": [
    {
      "width": 640,
      "height": 320,
      "title": "Counts of AutoVis vs Other (stacked by which field(s) matched)",
      "transform": [
        { "aggregate": [{ "op": "count", "as": "count" }], "groupby": ["match_label", "match_field"] }
      ],
      "mark": {
        "type": "bar",
        "cornerRadiusEnd": 3
      },
      "encoding": {
        "x": { "field": "match_label", "type": "nominal", "axis": { "title": "Match label" } },
        "y": { "field": "count", "type": "quantitative", "axis": { "title": "Number of papers" } },
        "color": { "field": "match_field", "type": "nominal", "legend": { "title": "Matched field(s)" } },
        "tooltip": [
          { "field": "match_label", "type": "nominal", "title": "Match" },
          { "field": "match_field", "type": "nominal", "title": "Matched field(s)" },
          { "field": "count", "type": "quantitative", "title": "Count" }
        ]
      }
    },

    {
      "width": 640,
      "height": 140,
      "title": "Example AutoVis papers (up to 5)",
      "transform": [
        { "filter": "datum.match_label === 'AutoVis'" },
        { "window": [{ "op": "row_number", "as": "rownum" }], "sort": [{ "field": "Year", "order": "descending" }] },
        { "filter": "datum.rownum <= 5" }
      ],
      "mark": { "type": "text", "align": "left", "baseline": "middle", "dx": 4 },
      "encoding": {
        "y": { "field": "rownum", "type": "ordinal", "axis": null },
        "text": { "field": "Title", "type": "nominal" },
        "color": { "value": "#333" },
        "tooltip": [
          { "field": "Title", "type": "nominal" },
          { "field": "AuthorNames", "type": "nominal", "title": "Authors" },
          { "field": "match_field", "type": "nominal", "title": "Matched field(s)" }
        ]
      }
    }
  ]
}

