{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "data": {
    "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv",
    "format": { "type": "csv" }
  },
  "width": 800,
  "height": 400,
  "title": "AutoVis papers: annual counts and share of all Vis publications (3-yr rolling mean shown for share)",
  "transform": [
    {
      "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, (datum['AuthorKeywords'] || '') + ' ' + (datum['Abstract'] || '') + ' ' + (datum['Title'] || '')) ? 1 : 0",
      "as": "AutoVisFlag"
    },
    { "calculate": "toNumber(datum.Year)", "as": "YearNum" },
    {
      "aggregate": [
        { "op": "sum", "field": "AutoVisFlag", "as": "AutoVis_Count" },
        { "op": "count", "as": "Total_Vis_Count" }
      ],
      "groupby": ["Year", "YearNum"]
    },
    {
      "calculate": "datum.AutoVis_Count / datum.Total_Vis_Count * 100",
      "as": "AutoVis_Share_percent"
    },
    {
      "window": [
        { "op": "mean", "field": "AutoVis_Count", "as": "AutoVis_Count_Rolling" },
        { "op": "mean", "field": "AutoVis_Share_percent", "as": "AutoVis_Share_percent_Rolling" }
      ],
      "frame": [-1, 1],
      "sort": [{ "field": "YearNum", "order": "ascending" }]
    }
  ],
  "layer": [
    {
      "mark": { "type": "bar" },
      "encoding": {
        "x": { "field": "Year", "type": "ordinal", "axis": { "title": "Year", "labelAngle": 0 } },
        "y": { "field": "AutoVis_Count", "type": "quantitative", "axis": { "title": "AutoVis papers (count)", "orient": "left" } },
        "color": { "value": "#4C78A8" },
        "tooltip": [
          { "field": "Year", "type": "ordinal", "title": "Year" },
          { "field": "AutoVis_Count", "type": "quantitative", "title": "AutoVis_Count" },
          { "field": "Total_Vis_Count", "type": "quantitative", "title": "Total_Vis_Count" },
          { "field": "AutoVis_Share_percent", "type": "quantitative", "title": "AutoVis_Share (%)", "format": ".2f" },
          { "field": "AutoVis_Count_Rolling", "type": "quantitative", "title": "AutoVis_Count (3-yr avg)", "format": ".2f" }
        ]
      }
    },
    {
      "mark": { "type": "line", "point": true, "interpolate": "monotone" },
      "encoding": {
        "x": { "field": "Year", "type": "ordinal" },
        "y": { "field": "AutoVis_Share_percent_Rolling", "type": "quantitative", "axis": { "title": "AutoVis share (%)", "orient": "right" } },
        "color": { "value": "#E45756" },
        "tooltip": [
          { "field": "Year", "type": "ordinal", "title": "Year" },
          { "field": "AutoVis_Share_percent", "type": "quantitative", "title": "AutoVis_Share (%) (raw)", "format": ".2f" },
          { "field": "AutoVis_Share_percent_Rolling", "type": "quantitative", "title": "AutoVis_Share (%) (3-yr avg)", "format": ".2f" }
        ]
      }
    }
  ],
  "resolve": { "scale": { "y": "independent" } }
}

{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "AutoVis yearly counts and share with 3-year rolling averages. Detection method: 3-year rolling average + detect years where year-over-year pct change > 50% for two consecutive years. Ignore candidate annotations for years with Total_Count < 5 or AutoVis_Count < 2.",
  "title": "AutoVis papers by year — detection: 3-year rolling avg; flag if YoY % change > 50% for two consecutive years (Total_Count >= 5 & AutoVis_Count >= 2)",
  "data": { "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv" },
  "transform": [
    {
      "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, (datum['AuthorKeywords'] || '') + ' ' + (datum['Abstract'] || '') + ' ' + (datum['Title'] || '')) ? 1 : 0",
      "as": "is_autovis"
    },
    {
      "aggregate": [
        { "op": "count", "as": "Total_Count" },
        { "op": "sum", "field": "is_autovis", "as": "AutoVis_Count" }
      ],
      "groupby": ["Year"]
    },
    {
      "calculate": "datum.AutoVis_Count / datum.Total_Count",
      "as": "Share"
    },
    {
      "window": [
        { "op": "mean", "field": "AutoVis_Count", "as": "AutoVis_Rolling3" },
        { "op": "mean", "field": "Share", "as": "Share_Rolling3" }
      ],
      "frame": [-1, 1],
      "sort": [{ "field": "Year", "order": "ascending" }]
    },
    {
      "window": [
        { "op": "lag", "field": "AutoVis_Count", "as": "lag_AutoVis" },
        { "op": "lead", "field": "AutoVis_Count", "as": "lead_AutoVis" }
      ],
      "sort": [{ "field": "Year", "order": "ascending" }]
    },
    {
      "calculate": "datum.lag_AutoVis > 0 ? (datum.AutoVis_Count - datum.lag_AutoVis) / datum.lag_AutoVis : null",
      "as": "pct_change"
    },
    {
      "calculate": "datum.AutoVis_Count > 0 && datum.lead_AutoVis != null ? (datum.lead_AutoVis - datum.AutoVis_Count) / datum.AutoVis_Count : null",
      "as": "lead_pct_change"
    },
    {
      "calculate": "(datum.pct_change != null && datum.lead_pct_change != null && datum.pct_change > 0.5 && datum.lead_pct_change > 0.5 && datum.Total_Count >= 5 && datum.AutoVis_Count >= 2) ? 1 : 0",
      "as": "notable_flag"
    }
  ],
  "layer": [
    {
      "mark": { "type": "line", "point": false, "tooltip": true },
      "encoding": {
        "x": { "field": "Year", "type": "ordinal", "axis": { "title": "Year" } },
        "y": { "field": "AutoVis_Count", "type": "quantitative", "axis": { "title": "AutoVis count (left)" } },
        "color": { "value": "#1f77b4" },
        "tooltip": [
          { "field": "Year", "type": "ordinal", "title": "Year" },
          { "field": "AutoVis_Count", "type": "quantitative", "title": "AutoVis_Count" },
          { "field": "Total_Count", "type": "quantitative", "title": "Total_Count" },
          { "field": "Share", "type": "quantitative", "title": "Share" }
        ]
      }
    },
    {
      "mark": { "type": "line", "strokeDash": [4,2], "opacity": 0.9 },
      "encoding": {
        "x": { "field": "Year", "type": "ordinal" },
        "y": { "field": "AutoVis_Rolling3", "type": "quantitative" },
        "color": { "value": "#1f77b4" },
        "opacity": { "value": 0.8 }
      }
    },
    {
      "mark": { "type": "line" },
      "encoding": {
        "x": { "field": "Year", "type": "ordinal" },
        "y": { "field": "Share_Rolling3", "type": "quantitative", "axis": { "title": "Share (right)", "orient": "right" } },
        "color": { "value": "#ff7f0e" }
      }
    },
    {
      "transform": [{ "filter": "datum.notable_flag == 1" }],
      "layer": [
        {
          "mark": { "type": "rule", "color": "red", "opacity": 0.7 },
          "encoding": {
            "x": { "field": "Year", "type": "ordinal" }
          }
        },
        {
          "mark": { "type": "point", "filled": true, "color": "red", "size": 80 },
          "encoding": {
            "x": { "field": "Year", "type": "ordinal" },
            "y": { "field": "AutoVis_Count", "type": "quantitative" },
            "tooltip": [
              { "field": "Year", "type": "ordinal" },
              { "field": "AutoVis_Count", "type": "quantitative" },
              { "field": "Total_Count", "type": "quantitative" },
              { "field": "Share", "type": "quantitative" },
              { "field": "pct_change", "type": "quantitative", "title": "YoY % change (prev)" },
              { "field": "lead_pct_change", "type": "quantitative", "title": "YoY % change (next)" }
            ]
          }
        },
        {
          "mark": { "type": "text", "dy": -12, "color": "red", "fontWeight": "bold" },
          "encoding": {
            "x": { "field": "Year", "type": "ordinal" },
            "y": { "field": "AutoVis_Count", "type": "quantitative" },
            "text": { "field": "Year", "type": "ordinal" }
          }
        }
      ]
    }
  ],
  "resolve": { "scale": { "y": "independent" }, "axis": { "y": "independent" } },
  "config": {
    "view": { "stroke": "transparent" },
    "axis": { "labelFontSize": 11, "titleFontSize": 12 }
  }
}

