    {
      "calculate": "test(/automatic vis|automated vis|visualization recommendation|mixed initiative|mixed-initiative|visualization generation|vis generation|agent/i, (datum.AuthorKeywords || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.Title || '')) ? 'AutoVis' : 'Other'",
      "as": "AutoVis"
    },
    {
      "calculate": "(!isNaN(toNumber(datum.AminerCitationCount)) ? toNumber(datum.AminerCitationCount) : 0)",
      "as": "AminerCitationCountNum"
    },
    {
      "calculate": "(!isNaN(toNumber(datum.CitationCount_CrossRef)) ? toNumber(datum.CitationCount_CrossRef) : 0)",
      "as": "CrossRefCitationCountNum"
    },
    {
      "calculate": "(!isNaN(toNumber(datum.Year)) ? toNumber(datum.Year) : 2025)",
      "as": "PubYearNum"
    },
    {
      "calculate": "(AminerCitationCountNum) / (2025 - PubYearNum + 1)",
      "as": "cpy_Aminer"
    },
    {
      "calculate": "(CrossRefCitationCountNum) / (2025 - PubYearNum + 1)",
      "as": "cpy_CrossRef"
    },
    {
      "joinaggregate": [
        {
          "op": "median",
          "field": "cpy_Aminer",
          "as": "median_cpy"
        }
      ]
    },
    {
      "calculate": "datum.cpy_Aminer >= 2 * datum.median_cpy",
      "as": "fast_rising"
    }
  ],
  "mark": "point",
  "encoding": {
    "x": {
      "field": "PubYearNum",
      "type": "quantitative",
      "title": "Publication year"
