{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "data": { "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv" },
  "vconcat": [
    {
      "height": 60,
      "width": 700,
      "description": "Count and proportion of InternalReferences that map to DOIs present in the dataset.",
      "transform": [
        { "calculate": "datum.InternalReferences ? split(datum.InternalReferences, ';') : []", "as": "refs_array" },
        { "flatten": ["refs_array"], "as": ["ref"] },
        { "calculate": "trim(datum.ref)", "as": "ref_trim" },
        { "filter": "datum.ref_trim != null && datum.ref_trim != ''" },
        {
          "lookup": "ref_trim",
          "from": {
            "data": { "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv" },
            "key": "DOI",
            "fields": ["DOI"]
          },
          "as": ["matchedDOI"]
        },
        { "calculate": "datum.matchedDOI != null ? 1 : 0", "as": "matched_num" },
        { "aggregate": [
          { "op": "sum", "field": "matched_num", "as": "matched_count" },
          { "op": "count", "as": "total_refs" }
        ] },
        { "calculate": "datum.total_refs ? round(datum.matched_count / datum.total_refs * 100) : 0", "as": "mapped_pct" },
        { "calculate": "'Mapped internal refs: ' + datum.matched_count + ' / ' + datum.total_refs + ' (' + datum.mapped_pct + '%)'", "as": "label" }
      ],
      "mark": { "type": "text", "align": "left", "baseline": "middle", "fontSize": 14 },
      "encoding": {
        "text": { "field": "label", "type": "nominal" },
        "x": { "value": 5 },
        "y": { "value": 20 }
      }
    },

    {
      "height": 320,
      "width": 700,
      "description": "Distribution of number of distinct venues per deduped author (used to choose threshold for cross-venue).",
      "transform": [
        { "filter": "datum['AuthorNames-Deduped'] != null && datum['AuthorNames-Deduped'] != ''" },
        { "aggregate": [ { "op": "count", "as": "papers" } ], "groupby": ["AuthorNames-Deduped", "Conference"] },
        { "aggregate": [ { "op": "count", "as": "venues_count" } ], "groupby": ["AuthorNames-Deduped"] },
        { "calculate": "datum.venues_count >= 2 ? '>\u003d2' : '<2'", "as": "cross_flag" }
      ],
      "layer": [
        {
          "mark": "bar",
          "encoding": {
            "x": {
              "field": "venues_count",
              "type": "quantitative",
              "bin": { "step": 1 },
              "title": "Distinct venues per author"
            },
            "y": {
              "aggregate": "count",
              "type": "quantitative",
              "title": "Number of authors"
            },
            "color": {
              "field": "cross_flag",
              "type": "nominal",
              "scale": { "domain": ["<2", ">=2"], "range": ["#6baed6", "#fe9929"] },
              "legend": { "title": "Authors publishing in >=2 venues?" }
            },
            "tooltip": [
              { "field": "venues_count", "type": "quantitative", "title": "Venues per author" },
              { "aggregate": "count", "type": "quantitative", "title": "Authors" }
            ]
          }
        },
        {
          "mark": { "type": "text", "dy": -8, "fontSize": 11 },
          "encoding": {
            "text": { "aggregate": "count", "type": "quantitative", "title": "Authors" },
            "x": { "field": "venues_count", "type": "quantitative", "bin": { "step": 1 } },
            "y": { "aggregate": "count", "type": "quantitative" }
          }
        }
      ]
    }
  ],
  "resolve": { "scale": { "x": "independent" } }
}

