{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "vconcat": [
    {
      "title": "AutoVis vs Other (deduplicated) — counts and data-quality summary",
      "data": {
        "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv",
        "format": { "type": "csv" }
      },
      "transform": [
        {
          "calculate": "(datum.Title || \"\") + \" \" + (datum.Abstract || \"\") + \" \" + (datum.AuthorKeywords || \"\")",
          "as": "combinedText"
        },
        {
          "calculate": "(indexof(lower((datum.combinedText || \"\")), \"automatic vis\") >= 0) || (indexof(lower((datum.combinedText || \"\")), \"automated vis\") >= 0) || (indexof(lower((datum.combinedText || \"\")), \"visualization recommendation\") >= 0) || (indexof(lower((datum.combinedText || \"\")), \"mixed initiative\") >= 0) || (indexof(lower((datum.combinedText || \"\")), \"mixed-initiative\") >= 0) || (indexof(lower((datum.combinedText || \"\")), \"visualization generation\") >= 0) || (indexof(lower((datum.combinedText || \"\")), \"vis generation\") >= 0) || (indexof(lower((datum.combinedText || \"\")), \"agent\") >= 0)",
          "as": "AutoVis"
        },
        {
          "calculate": "(datum.Year == null || datum.Year == \"\") ? 1 : 0",
          "as": "YearMissing"
        },
        {
          "calculate": "(datum.AuthorKeywords == null || datum.AuthorKeywords == \"\") ? 1 : 0",
          "as": "AKMissing"
        },
        {
          "calculate": "trim(replace(replace(replace(replace(replace(replace(lower((datum.Title || \"\")), \".\", \" \"), \",\", \" \"), \":\", \" \"), \";\", \" \"), \"(\", \" \"), \")\", \" \"))",
          "as": "normTitle"
        },
        {
          "calculate": "((datum.DOI || \"\") != \"\") ? trim(datum.DOI || \"\") : (datum.normTitle || \"\")",
          "as": "dedupeKey"
        },
        {
          "filter": "datum.dedupeKey != null && datum.dedupeKey != \"\""
        },
        {
          "aggregate": [
            { "op": "count", "as": "_count" },
            { "op": "max", "field": "YearMissing", "as": "YearMissingDedup" },
            { "op": "max", "field": "AKMissing", "as": "AKMissingDedup" }
          ],
          "groupby": ["dedupeKey", "AutoVis"]
        },
        {
          "aggregate": [
            { "op": "sum", "field": "_count", "as": "docs" },
            { "op": "sum", "field": "YearMissingDedup", "as": "totalYearMissing" },
            { "op": "sum", "field": "AKMissingDedup", "as": "totalAKMissing" }
          ],
          "groupby": ["AutoVis"]
        },
        {
          "joinaggregate": [
            { "op": "sum", "field": "docs", "as": "deduped_total" },
            { "op": "sum", "field": "totalYearMissing", "as": "sumYearMissing" },
            { "op": "sum", "field": "totalAKMissing", "as": "sumAKMissing" }
          ]
        },
        {
          "calculate": "toString(datum.docs || 0)",
          "as": "docs_str"
        },
        {
          "calculate": "toString(datum.deduped_total || 0) + \" deduped records; Year missing: \" + toString(datum.sumYearMissing || 0) + \" (\" + toString(round(((datum.sumYearMissing || 0) / (datum.deduped_total || 1)) * 100, 1)) + \"%)\" + \"; AuthorKeywords missing: \" + toString(datum.sumAKMissing || 0) + \" (\" + toString(round(((datum.sumAKMissing || 0) / (datum.deduped_total || 1)) * 100, 1)) + \"%)\"",
          "as": "label"
        }
      ],
