        {
          "mark": { "type": "bar", "cornerRadius": 3 },
          "encoding": {
            "x": { "field": "AutoVis", "type": "nominal", "title": "AutoVis match" },
            "y": { "field": "docs", "type": "quantitative", "title": "Number of deduplicated records" },
            "color": { "field": "AutoVis", "type": "nominal", "legend": null },
            "tooltip": [
              { "field": "AutoVis", "type": "nominal" },
              { "field": "docs", "type": "quantitative", "title": "deduped_count" },
              { "field": "totalYearMissing", "type": "quantitative", "title": "year_missing_count" },
              { "field": "totalAKMissing", "type": "quantitative", "title": "authorkeywords_missing_count" }
            ]
          }
        },
        {
          "mark": { "type": "text", "align": "right", "baseline": "bottom", "dx": -4, "dy": -4, "color": "#333" },
          "encoding": {
            "text": { "field": "label", "type": "nominal" }
          }
        }
      ],
      "width": 700,
      "height": 240
    },
    {
      "title": "Volume by Year (deduplicated) — AutoVis vs Other",
      "data": {
        "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv",
