{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "data": {
    "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv",
    "format": { "type": "csv" }
  },
  "vconcat": [
    {
      "width": 700,
      "height": 320,
      "title": "Number of AutoVis vs Other Vis Papers per Year",
      "transform": [
        { "filter": "datum.Conference != null && datum.Conference != '' && indexof(lower(datum.Conference || ''), 'vis') >= 0" },
        { "filter": "datum.Year != null && datum.Year != ''" },
        { "calculate": "toNumber(datum.Year)", "as": "YearNum" },
        { "calculate": "(indexof(lower(datum.Title || ''), 'autovis') >= 0) || (indexof(lower(datum['AuthorKeywords'] || ''), 'autovis') >= 0) || (indexof(lower(datum.Abstract || ''), 'autovis') >= 0) ? 'AutoVis' : 'Other'", "as": "Category" },
        { "aggregate": [{ "op": "count", "as": "count" }], "groupby": ["YearNum", "Category"] }
      ],
      "mark": { "type": "bar", "tooltip": true },
      "encoding": {
        "x": { "field": "YearNum", "type": "quantitative", "title": "Year", "axis": { "format": "d", "labelAngle": 0 } },
        "y": { "field": "count", "type": "quantitative", "title": "Number of Papers" },
        "color": { "field": "Category", "type": "nominal", "title": "Category", "scale": { "domain": ["AutoVis", "Other"], "range": ["#1f77b4", "#bdbdbd"] } },
        "tooltip": [
          { "field": "YearNum", "type": "quantitative", "title": "Year" },
          { "field": "Category", "type": "nominal", "title": "Category" },
          { "field": "count", "type": "quantitative", "title": "Papers" }
        ]
      }
    },
    {
      "width": 700,
      "height": 160,
      "title": "Share of AutoVis Papers Over Time",
      "transform": [
        { "filter": "datum.Conference != null && datum.Conference != '' && indexof(lower(datum.Conference || ''), 'vis') >= 0" },
        { "filter": "datum.Year != null && datum.Year != ''" },
        { "calculate": "toNumber(datum.Year)", "as": "YearNum" },
        { "calculate": "(indexof(lower(datum.Title || ''), 'autovis') >= 0) || (indexof(lower(datum['AuthorKeywords'] || ''), 'autovis') >= 0) || (indexof(lower(datum.Abstract || ''), 'autovis') >= 0) ? 'AutoVis' : 'Other'", "as": "Category" },
        { "aggregate": [{ "op": "count", "as": "count" }], "groupby": ["YearNum", "Category"] },
        { "joinaggregate": [{ "op": "sum", "field": "count", "as": "total_per_year" }], "groupby": ["YearNum"] },
        { "calculate": "toNumber(datum.count) / (datum.total_per_year == 0 ? 1 : toNumber(datum.total_per_year))", "as": "share" },
        { "calculate": "toNumber(datum.share) * 100", "as": "share_pct" },
        { "filter": "datum.Category == 'AutoVis'" }
      ],
      "mark": { "type": "line", "point": true },
      "encoding": {
        "x": { "field": "YearNum", "type": "quantitative", "title": "Year", "axis": { "format": "d", "labelAngle": 0 } },
        "y": { "field": "share_pct", "type": "quantitative", "title": "AutoVis share (%)", "scale": { "domain": [0, 100] } },
        "tooltip": [
          { "field": "YearNum", "type": "quantitative", "title": "Year" },
          { "field": "share_pct", "type": "quantitative", "title": "Share (%)" }
        ],
        "color": { "value": "#d62728" }
      }
    }
  ],
  "config": {
    "axis": { "labelFontSize": 12, "titleFontSize": 13 },
    "legend": { "titleFontSize": 12, "labelFontSize": 11 }
  }
}

