{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Author productivity vs citation impact (top authors) from the AutoVis subset. Each author is exploded from AuthorNames-Deduped; Institution is parsed from the first AuthorAffiliation entry.",
  "data": {
    "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv",
    "format": { "type": "csv" }
  },
  "transform": [
    { "filter": "datum['AuthorNames-Deduped'] != null && datum['AuthorNames-Deduped'] != ''" },
    { "calculate": "split((datum['AuthorNames-Deduped'] || ''), ';')", "as": "authors_array" },
    { "calculate": "split((datum['AuthorAffiliation'] || ''), ';')", "as": "affiliations_array" },
    { "flatten": ["authors_array"] },
    { "calculate": "trim((datum['authors_array'] || ''))", "as": "Author" },
    {
      "calculate": "indexof(((datum['affiliations_array'] || [])[0] || ''), ',') >= 0 ? trim(substring(((datum['affiliations_array'] || [])[0] || ''), 0, indexof(((datum['affiliations_array'] || [])[0] || ''), ','))) : trim(((datum['affiliations_array'] || [])[0] || ''))",
