{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "vconcat": [
    {
      "title": "AutoVis papers by Conference (keyword-based label)",
      "width": 600,
      "height": 300,
      "data": {
        "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv"
      },
      "transform": [
        {
          "filter": "(datum.AuthorKeywords != null && datum.AuthorKeywords != '') || (datum.Abstract != null && datum.Abstract != '') || (datum.Title != null && datum.Title != '')"
        },
        {
          "calculate": "toNumber(datum.Year)",
          "as": "YearNum"
        },
        {
          "calculate": "lower((datum.AuthorKeywords || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.Title || ''))",
          "as": "combText"
        },
        {
          "calculate": "(indexof(datum.combText, 'automatic vis') >= 0 || indexof(datum.combText, 'automated vis') >= 0 || indexof(datum.combText, 'visualization recommendation') >= 0 || indexof(datum.combText, 'mixed initiative') >= 0 || indexof(datum.combText, 'mixed-initiative') >= 0 || indexof(datum.combText, 'visualization generation') >= 0 || indexof(datum.combText, 'vis generation') >= 0 || indexof(datum.combText, 'agent') >= 0) ? 'AutoVis' : 'Other'",
          "as": "AutoVisLabel"
        },
        {
          "filter": "datum.AutoVisLabel == 'AutoVis'"
        },
        {
          "aggregate": [{ "op": "count", "as": "Count" }],
          "groupby": ["Conference"]
        }
      ],
      "mark": {
        "type": "bar",
        "tooltip": true
      },
      "encoding": {
        "x": {
          "field": "Conference",
          "type": "nominal",
          "axis": { "labelAngle": -40, "labelExpr": "datum.value" },
          "title": "Conference"
        },
        "y": {
          "field": "Count",
          "type": "quantitative",
          "title": "Number of AutoVis papers"
        },
        "tooltip": [
          { "field": "Conference", "type": "nominal", "title": "Conference" },
          { "field": "Count", "type": "quantitative", "title": "AutoVis Count" }
        ]
      }
    },
    {
      "title": "AutoVis proportion and count over Years",
      "width": 600,
      "height": 240,
      "data": {
        "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv"
      },
      "transform": [
        {
          "filter": "(datum.AuthorKeywords != null && datum.AuthorKeywords != '') || (datum.Abstract != null && datum.Abstract != '') || (datum.Title != null && datum.Title != '')"
        },
        {
          "calculate": "toNumber(datum.Year)",
          "as": "YearNum"
        },
        {
          "calculate": "lower((datum.AuthorKeywords || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.Title || ''))",
          "as": "combText"
        },
        {
          "calculate": "(indexof(datum.combText, 'automatic vis') >= 0 || indexof(datum.combText, 'automated vis') >= 0 || indexof(datum.combText, 'visualization recommendation') >= 0 || indexof(datum.combText, 'mixed initiative') >= 0 || indexof(datum.combText, 'mixed-initiative') >= 0 || indexof(datum.combText, 'visualization generation') >= 0 || indexof(datum.combText, 'vis generation') >= 0 || indexof(datum.combText, 'agent') >= 0) ? 'AutoVis' : 'Other'",
          "as": "AutoVisLabel"
        },
        {
          "aggregate": [{ "op": "count", "as": "Count" }],
          "groupby": ["YearNum", "AutoVisLabel"]
        },
        {
          "joinaggregate": [{ "op": "sum", "field": "Count", "as": "TotalPerYear" }],
          "groupby": ["YearNum"]
        },
        {
          "calculate": "toNumber(datum.Count) / toNumber(datum.TotalPerYear)",
          "as": "Proportion"
        },
        {
          "filter": "datum.AutoVisLabel == 'AutoVis'"
        }
      ],
      "layer": [
        {
          "mark": {
            "type": "line",
            "point": true
          },
          "encoding": {
            "x": { "field": "YearNum", "type": "quantitative", "title": "Year" },
            "y": { "field": "Proportion", "type": "quantitative", "title": "Proportion of AutoVis papers", "axis": { "format": ".0%" } },
            "tooltip": [
              { "field": "YearNum", "type": "quantitative", "title": "Year" },
              { "field": "Count", "type": "quantitative", "title": "AutoVis Count" },
              { "field": "TotalPerYear", "type": "quantitative", "title": "Total Papers (Year)" },
              { "field": "Proportion", "type": "quantitative", "title": "Proportion", "format": ".1%" }
            ]
          }
        },
        {
          "mark": { "type": "bar", "opacity": 0.3 },
          "encoding": {
            "x": { "field": "YearNum", "type": "quantitative" },
            "y": { "field": "Count", "type": "quantitative", "title": "AutoVis Count" },
            "opacity": { "value": 0.25 },
            "tooltip": [
              { "field": "YearNum", "type": "quantitative", "title": "Year" },
              { "field": "Count", "type": "quantitative", "title": "AutoVis Count" }
            ]
          }
        }
      ]
    }
  ]
}

{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Missingness summary for key text and citation fields + availability combinations for citation-count fields.",
  "data": {"url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv", "format": {"type": "csv"}},
  "vconcat": [
    {
      "width": 700,
      "height": 220,
      "title": "Missingness rate by attribute (0 = present, 1 = missing)",
      "transform": [
        {"calculate": "((datum.AuthorNames || '') != '') ? 0 : 1", "as": "authornames_miss"},
        {"calculate": "((datum[\"AuthorNames-Deduped\"] || '') != '') ? 0 : 1", "as": "authornames_deduped_miss"},
        {"calculate": "((datum.AuthorAffiliation || '') != '') ? 0 : 1", "as": "authoraffil_miss"},
        {"calculate": "((datum.AuthorKeywords || '') != '') ? 0 : 1", "as": "authorkeywords_miss"},
        {"calculate": "((datum.Abstract || '') != '') ? 0 : 1", "as": "abstract_miss"},
        {"calculate": "((datum.AminerCitationCount || '') != '') ? 0 : 1", "as": "aminercite_miss"},
        {"calculate": "((datum.CitationCount_CrossRef || '') != '') ? 0 : 1", "as": "crossref_miss"},
        {"fold": ["authornames_miss", "authornames_deduped_miss", "authoraffil_miss", "authorkeywords_miss", "abstract_miss", "aminercite_miss", "crossref_miss"], "as": ["attribute", "missing"]},
        {"aggregate": [{"op": "mean", "field": "missing", "as": "missing_rate"}], "groupby": ["attribute"]},
        {"calculate": "datum.missing_rate", "as": "missing_rate"}
      ],
      "mark": {"type": "bar", "tooltip": true},
      "encoding": {
        "y": {"field": "attribute", "type": "nominal", "axis": {"title": "Attribute"}},
        "x": {"field": "missing_rate", "type": "quantitative", "axis": {"title": "Missing rate", "format": ".0%"}},
        "color": {"field": "missing_rate", "type": "quantitative", "legend": null}
      }
    },
    {
      "width": 700,
      "height": 220,
      "title": "Availability of citation-count fields (Aminer vs CrossRef)",
      "transform": [
        {"calculate": "(((datum.AminerCitationCount || '') != '') && ((datum.CitationCount_CrossRef || '') != '')) ? 'Both' : ((((datum.AminerCitationCount || '') != '') ? 'Aminer only' : (((datum.CitationCount_CrossRef || '') != '') ? 'CrossRef only' : 'Neither')))", "as": "citations_availability"},
        {"aggregate": [{"op": "count", "as": "count"}], "groupby": ["citations_availability"]}
      ],
      "mark": {"type": "bar", "tooltip": true},
      "encoding": {
        "x": {"field": "citations_availability", "type": "nominal", "axis": {"title": "Citation fields available"}},
        "y": {"field": "count", "type": "quantitative", "axis": {"title": "Number of records"}},
        "color": {"field": "citations_availability", "type": "nominal", "legend": null}
      }
    }
  ]
}

