{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Timeline of AutoVis papers with highlighted milestone papers selected by a combined score (citations, downloads, award). Top-ranked papers are emphasized.",
  "width": 800,
  "height": 360,
  "data": {
    "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv",
    "format": {"type": "csv"}
  },
  "transform": [
    {"filter": "datum.Year != null && datum.Year != ''"},
    {"calculate": "datum.CitationCount_CrossRef != null && datum.CitationCount_CrossRef != '' ? toNumber(datum.CitationCount_CrossRef) : 0", "as": "crossref"},
    {"calculate": "datum.AminerCitationCount != null && datum.AminerCitationCount != '' ? toNumber(datum.AminerCitationCount) : 0", "as": "aminer"},
    {"calculate": "datum.Downloads_Xplore != null && datum.Downloads_Xplore != '' ? toNumber(datum.Downloads_Xplore) : 0", "as": "downloads"},
    {"calculate": "datum.Award != null && datum.Award != ''", "as": "hasAward"},
    {"calculate": "(datum.crossref || 0) + (datum.aminer || 0) * 0.5 + (datum.downloads || 0) / 1000 * 0.1 + (datum.hasAward ? 1000 : 0)", "as": "score"},
    {"window": [{"op": "rank", "as": "rank"}], "sort": [{"field": "score", "order": "descending"}]},
    {"calculate": "(datum.Title || '')", "as": "title_clean"}
  ],
  "layer": [
    {
      "mark": {"type": "point", "filled": true, "opacity": 0.6},
      "encoding": {
        "x": {"field": "Year", "type": "quantitative", "title": "Year"},
        "y": {"field": "crossref", "type": "quantitative", "title": "CrossRef Citations"},
        "size": {"value": 60},
        "color": {"condition": {"test": "datum.hasAward","value": "#d62728"}, "value": "#bdbdbd"},
        "tooltip": [
          {"field": "title_clean", "type": "nominal", "title": "Title"},
          {"field": "Year", "type": "quantitative", "title": "Year"},
          {"field": "crossref", "type": "quantitative", "title": "CrossRef citations"},
          {"field": "aminer", "type": "quantitative", "title": "Aminer citations"},
          {"field": "downloads", "type": "quantitative", "title": "Downloads (Xplore)"},
          {"field": "Award", "type": "nominal", "title": "Award"},
          {"field": "score", "type": "quantitative", "title": "Selection score"},
          {"field": "rank", "type": "quantitative", "title": "Rank"}
        ]
      }
    },
    {
      "transform": [{"filter": "datum.rank <= 8"}],
      "layer": [
        {
          "mark": {"type": "point", "filled": true, "shape": "diamond", "opacity": 1},
          "encoding": {
            "x": {"field": "Year", "type": "quantitative"},
            "y": {"field": "crossref", "type": "quantitative"},
            "size": {"value": 220},
            "color": {"field": "hasAward", "type": "nominal", "scale": {"domain": [true, false], "range": ["#d62728", "#1f77b4"]}, "title": "Award"},
            "tooltip": [
              {"field": "title_clean", "type": "nominal", "title": "Title"},
              {"field": "Year", "type": "quantitative", "title": "Year"},
              {"field": "crossref", "type": "quantitative", "title": "CrossRef citations"},
              {"field": "aminer", "type": "quantitative", "title": "Aminer citations"},
              {"field": "downloads", "type": "quantitative", "title": "Downloads (Xplore)"},
              {"field": "Award", "type": "nominal", "title": "Award"},
              {"field": "score", "type": "quantitative", "title": "Selection score"},
              {"field": "rank", "type": "quantitative", "title": "Rank"}
            ]
          }
        },
        {
          "mark": {"type": "text", "dy": -12, "fontWeight": "bold", "fontSize": 11},
          "encoding": {
            "x": {"field": "Year", "type": "quantitative"},
            "y": {"field": "crossref", "type": "quantitative"},
            "text": {"field": "title_clean", "type": "nominal"},
            "color": {"value": "#222"}
          }
        }
      ]
    }
  ],
  "config": {
    "axis": {"labelFontSize": 11, "titleFontSize": 12},
    "legend": {"titleFontSize": 12, "labelFontSize": 11}
  }
}

