        { "filter": "datum.CitationCount_CrossRef != null && datum.CitationCount_CrossRef != ''" },
        {
          "calculate": "(datum.CitationCount_CrossRef != null && datum.CitationCount_CrossRef != '') ? (log(1 + toNumber(datum.CitationCount_CrossRef)) / log(10)) : null",
          "as": "cit_log"
        },
        { "bin": { "maxbins": 30 }, "field": "cit_log", "as": ["cit_bin_start", "cit_bin_end"] },
        { "aggregate": [{ "op": "count", "as": "count" }], "groupby": ["cit_bin_start", "cit_bin_end"] },
        { "calculate": "(datum.cit_bin_start + datum.cit_bin_end) / 2", "as": "cit_bin_mid" },
        { "window": [{ "op": "sum", "field": "count", "as": "cum_count" }], "sort": [{ "field": "cit_bin_start", "order": "ascending" }] },
        { "joinaggregate": [{ "op": "sum", "field": "count", "as": "total_count" }] },
        { "calculate": "datum.cum_count / (datum.total_count == 0 ? 1 : datum.total_count)", "as": "cdf" }
      ],
      "layer": [
        {
          "mark": { "type": "bar", "tooltip": true },
          "encoding": {
            "x": {
              "field": "cit_bin_start",
