              { "field": "Downloads_Xplore", "type": "quantitative", "title": "Downloads (original)" },
              { "field": "cit_log", "type": "quantitative", "title": "Citations (log)" },
              { "field": "dl_log", "type": "quantitative", "title": "Downloads (log)" }
            ]
          }
        },
        {
          "transform": [ { "loess": "cit_log", "on": "Year", "as": ["Year", "cit_loess"] } ],
