SELECT *
FROM Papers
WHERE LOWER(
  COALESCE("AuthorKeywords", '') || ' ' || COALESCE(Abstract, '') || ' ' || COALESCE(Title, '')
) LIKE '%automatic vis%'
OR LOWER(
  COALESCE("AuthorKeywords", '') || ' ' || COALESCE(Abstract, '') || ' ' || COALESCE(Title, '')
) LIKE '%automated vis%'
OR LOWER(
  COALESCE("AuthorKeywords", '') || ' ' || COALESCE(Abstract, '') || ' ' || COALESCE(Title, '')
) LIKE '%visualization recommendation%'
OR LOWER(
  COALESCE("AuthorKeywords", '') || ' ' || COALESCE(Abstract, '') || ' ' || COALESCE(Title, '')
) LIKE '%mixed initiative%'
OR LOWER(
  COALESCE("AuthorKeywords", '') || ' ' || COALESCE(Abstract, '') || ' ' || COALESCE(Title, '')
) LIKE '%mixed-initiative%'
OR LOWER(
  COALESCE("AuthorKeywords", '') || ' ' || COALESCE(Abstract, '') || ' ' || COALESCE(Title, '')
) LIKE '%visualization generation%'
OR LOWER(
  COALESCE("AuthorKeywords", '') || ' ' || COALESCE(Abstract, '') || ' ' || COALESCE(Title, '')
) LIKE '%vis generation%'
OR LOWER(
  COALESCE("AuthorKeywords", '') || ' ' || COALESCE(Abstract, '') || ' ' || COALESCE(Title, '')
) LIKE '%agent%'
ORDER BY Year ASC;

