{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Annual counts and share of AutoVis papers over time with mean citations; and venue concentration (AutoVis counts by Conference).",
  "data": {
    "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv",
    "format": {"type": "csv"}
  },
  "vconcat": [
    {
      "width": 800,
      "height": 320,
      "title": "Annual paper counts (AutoVis vs Other) and mean citations",
      "layer": [
        {
          "transform": [
            {"filter": "datum.Year != null && datum.Year != ''"},
            {"calculate": "lower((datum.Title || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.AuthorKeywords || ''))", "as": "_text"},
            {"calculate": "indexof(datum._text, 'autovis') >= 0 || indexof(datum._text, 'autonom') >= 0 ? 'AutoVis' : 'Other'", "as": "AutoVis_label"},
            {"aggregate": [{"op": "count", "as": "count"}], "groupby": ["Year", "AutoVis_label"]},
            {"joinaggregate": [{"op": "sum", "field": "count", "as": "total_count_per_year"}], "groupby": ["Year"]},
            {"calculate": "datum.AutoVis_label === 'AutoVis' ? toNumber(datum.count) / toNumber(datum.total_count_per_year) : null", "as": "auto_vis_share"}
          ],
          "mark": {"type": "bar"},
          "encoding": {
            "x": {"field": "Year", "type": "quantitative", "axis": {"title": "Year", "labelAngle": 0}},
            "y": {"field": "count", "type": "quantitative", "axis": {"title": "Paper count"}},
            "color": {"field": "AutoVis_label", "type": "nominal", "legend": {"title": "Label"}, "scale": {"domain": ["AutoVis", "Other"], "range": ["#1f77b4", "#c7c7c7"]}}
          }
        },
        {
          "transform": [
            {"filter": "datum.Year != null && datum.Year != ''"},
            {"calculate": "toNumber(datum.CitationCount_CrossRef)", "as": "_CitationCount_CrossRef"},
            {"aggregate": [{"op": "mean", "field": "_CitationCount_CrossRef", "as": "mean_citations"}], "groupby": ["Year"]}
          ],
          "mark": {"type": "line", "color": "#d62728", "point": true},
          "encoding": {
            "x": {"field": "Year", "type": "quantitative"},
            "y": {"field": "mean_citations", "type": "quantitative", "axis": {"title": "Mean CitationCount_CrossRef"}}
          }
        },
        {
          "transform": [
            {"filter": "datum.Year != null && datum.Year != ''"},
            {"calculate": "lower((datum.Title || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.AuthorKeywords || ''))", "as": "_text"},
            {"calculate": "indexof(datum._text, 'autovis') >= 0 || indexof(datum._text, 'autonom') >= 0 ? 'AutoVis' : 'Other'", "as": "AutoVis_label"},
            {"aggregate": [{"op": "count", "as": "count"}], "groupby": ["Year", "AutoVis_label"]},
            {"joinaggregate": [{"op": "sum", "field": "count", "as": "total_count_per_year"}], "groupby": ["Year"]},
            {"calculate": "datum.AutoVis_label === 'AutoVis' ? toNumber(datum.count) / toNumber(datum.total_count_per_year) : null", "as": "auto_vis_share"},
            {"filter": "datum.AutoVis_label === 'AutoVis'"}
          ],
          "mark": {"type": "line", "color": "#2ca02c", "point": true},
          "encoding": {
            "x": {"field": "Year", "type": "quantitative"},
            "y": {"field": "auto_vis_share", "type": "quantitative", "axis": {"title": "AutoVis share (fraction)"}}
          }
        }
      ],
      "resolve": {"scale": {"y": "independent"}}
    },
    {
      "width": 800,
      "height": 240,
      "title": "Venue concentration: Total AutoVis papers by Conference",
      "transform": [
        {"filter": "(datum.Title != null && datum.Title != '') || (datum.Abstract != null && datum.Abstract != '') || (datum.AuthorKeywords != null && datum.AuthorKeywords != '')"},
        {"calculate": "lower((datum.Title || '') + ' ' + (datum.Abstract || '') + ' ' + (datum.AuthorKeywords || ''))", "as": "_text"},
        {"calculate": "indexof(datum._text, 'autovis') >= 0 || indexof(datum._text, 'autonom') >= 0 ? 'AutoVis' : 'Other'", "as": "AutoVis_label"},
        {"filter": "datum.AutoVis_label === 'AutoVis'"},
        {"filter": "datum.Conference != null && datum.Conference != ''"},
        {"aggregate": [{"op": "count", "as": "auto_count"}], "groupby": ["Conference"]}
      ],
      "mark": "bar",
      "encoding": {
        "x": {"field": "auto_count", "type": "quantitative", "axis": {"title": "Total AutoVis papers"}},
        "y": {"field": "Conference", "type": "nominal", "axis": {"title": "Conference"}, "sort": "-x"},
        "tooltip": [
          {"field": "Conference", "type": "nominal"},
          {"field": "auto_count", "type": "quantitative"}
        ]
      }
    }
  ]
}

