{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Top authors and institutions in AutoVis (paper count and citation sums). Data is filtered for AutoVis-related papers and exploded so each author–affiliation becomes a separate row.",
  "data": { "url": "https://raw.githubusercontent.com/demoPlz/mini-template/main/studio/dataset.csv", "format": { "type": "csv" } },
  "transform": [
    {
      "filter": "( (datum.Title != null && indexof(lower(datum.Title || ''), 'automatic vis') >= 0) || (datum.Abstract != null && indexof(lower(datum.Abstract || ''), 'automatic vis') >= 0) || (datum['AuthorKeywords'] != null && indexof(lower(datum['AuthorKeywords'] || ''), 'automatic vis') >= 0) || (datum.Title != null && indexof(lower(datum.Title || ''), 'automated vis') >= 0) || (datum.Abstract != null && indexof(lower(datum.Abstract || ''), 'automated vis') >= 0) || (datum['AuthorKeywords'] != null && indexof(lower(datum['AuthorKeywords'] || ''), 'automated vis') >= 0) || (datum.Title != null && indexof(lower(datum.Title || ''), 'visualization recommendation') >= 0) || (datum.Abstract != null && indexof(lower(datum.Abstract || ''), 'visualization recommendation') >= 0) || (datum['AuthorKeywords'] != null && indexof(lower(datum['AuthorKeywords'] || ''), 'visualization recommendation') >= 0) || (datum.Title != null && indexof(lower(datum.Title || ''), 'mixed initiative') >= 0) || (datum.Abstract != null && indexof(lower(datum.Abstract || ''), 'mixed initiative') >= 0) || (datum['AuthorKeywords'] != null && indexof(lower(datum['AuthorKeywords'] || ''), 'mixed initiative') >= 0) || (datum.Title != null && indexof(lower(datum.Title || ''), 'mixed-initiative') >= 0) || (datum.Abstract != null && indexof(lower(datum.Abstract || ''), 'mixed-initiative') >= 0) || (datum['AuthorKeywords'] != null && indexof(lower(datum['AuthorKeywords'] || ''), 'mixed-initiative') >= 0) || (datum.Title != null && indexof(lower(datum.Title || ''), 'visualization generation') >= 0) || (datum.Abstract != null && indexof(lower(datum.Abstract || ''), 'visualization generation') >= 0) || (datum['AuthorKeywords'] != null && indexof(lower(datum['AuthorKeywords'] || ''), 'visualization generation') >= 0) || (datum.Title != null && indexof(lower(datum.Title || ''), 'vis generation') >= 0) || (datum.Abstract != null && indexof(lower(datum.Abstract || ''), 'vis generation') >= 0) || (datum['AuthorKeywords'] != null && indexof(lower(datum['AuthorKeywords'] || ''), 'vis generation') >= 0) || (datum.Title != null && indexof(lower(datum.Title || ''), 'agent') >= 0) || (datum.Abstract != null && indexof(lower(datum.Abstract || ''), 'agent') >= 0) || (datum['AuthorKeywords'] != null && indexof(lower(datum['AuthorKeywords'] || ''), 'agent') >= 0) )"
    },
    {
      "filter": "datum['AuthorNames-Deduped'] != null && datum['AuthorNames-Deduped'] != ''"
    },
    {
      "calculate": "split(datum['AuthorNames-Deduped'] || '', ';')",
      "as": "authors_array"
    },
    {
      "calculate": "split(datum['AuthorAffiliation'] || '', ';')",
      "as": "affils_array"
    },
    {
      "calculate": "isArray(datum.authors_array) ? datum.authors_array : []",
      "as": "authors_array2"
    },
    {
      "calculate": "isArray(datum.affils_array) ? datum.affils_array : []",
      "as": "affils_array2"
    },
    {
      "flatten": ["authors_array2", "affils_array2"]
    },
    {
      "calculate": "trim(datum.authors_array2 || '')",
      "as": "author_clean"
    },
    {
      "calculate": "trim(datum.affils_array2 || '')",
      "as": "affil_clean"
    },
    {
      "calculate": "lower(datum.affil_clean || '')",
      "as": "affil_lower"
    },
    {
      "calculate": "var parts = split(datum.affil_lower, ','); trim(parts[parts.length-1] || parts[0] || '')",
      "as": "institution"
    },
    {
      "calculate": "toNumber(datum.AminerCitationCount || 0)",
      "as": "numeric_Aminer"
    },
    {
      "calculate": "toNumber(datum.CitationCount_CrossRef || 0)",
      "as": "numeric_CrossRef"
    },
    {
      "filter": "datum.author_clean != null && datum.author_clean != ''"
    }
  ],
  "vconcat": [
    {
      "title": "Top 10 Authors in AutoVis by Paper Count",
      "width": 700,
      "height": 320,
      "transform": [
        {
          "aggregate": [
            { "op": "count", "as": "paper_count" },
            { "op": "sum", "field": "numeric_Aminer", "as": "sum_citations_Aminer" },
            { "op": "sum", "field": "numeric_CrossRef", "as": "sum_citations_CrossRef" },
            { "op": "mean", "field": "numeric_Aminer", "as": "mean_citations_Aminer" }
          ],
          "groupby": ["author_clean"]
        },
        {
          "window": [ { "op": "row_number", "as": "rank" } ],
          "sort": [ { "field": "paper_count", "order": "descending" } ]
        },
        {
          "filter": "datum.rank <= 10"
        }
      ],
      "mark": { "type": "bar", "tooltip": true },
      "encoding": {
        "y": { "field": "author_clean", "type": "nominal", "title": "Author", "sort": "-x" },
        "x": { "field": "paper_count", "type": "quantitative", "title": "Paper Count" },
        "color": { "value": "#4C78A8" },
        "tooltip": [
          { "field": "author_clean", "type": "nominal", "title": "Author" },
          { "field": "paper_count", "type": "quantitative", "title": "Paper Count" },
          { "field": "sum_citations_Aminer", "type": "quantitative", "title": "Sum Aminer Citations" },
          { "field": "sum_citations_CrossRef", "type": "quantitative", "title": "Sum CrossRef Citations" },
          { "field": "mean_citations_Aminer", "type": "quantitative", "title": "Mean Aminer Citations" }
        ]
      }
    },
    {
      "title": "Top 10 Institutions in AutoVis by Paper Count",
      "width": 700,
      "height": 320,
      "transform": [
        {
          "aggregate": [
            { "op": "count", "as": "paper_count" },
            { "op": "sum", "field": "numeric_Aminer", "as": "sum_citations_Aminer" },
            { "op": "sum", "field": "numeric_CrossRef", "as": "sum_citations_CrossRef" },
            { "op": "mean", "field": "numeric_Aminer", "as": "mean_citations_Aminer" }
          ],
          "groupby": ["institution"]
        },
        {
          "window": [ { "op": "row_number", "as": "rank" } ],
          "sort": [ { "field": "paper_count", "order": "descending" } ]
        },
        {
          "filter": "datum.rank <= 10"
        }
      ],
      "mark": { "type": "bar", "tooltip": true },
      "encoding": {
        "y": { "field": "institution", "type": "nominal", "title": "Institution", "sort": "-x" },
        "x": { "field": "paper_count", "type": "quantitative", "title": "Paper Count" },
        "color": { "value": "#E45756" },
        "tooltip": [
          { "field": "institution", "type": "nominal", "title": "Institution" },
          { "field": "paper_count", "type": "quantitative", "title": "Paper Count" },
          { "field": "sum_citations_Aminer", "type": "quantitative", "title": "Sum Aminer Citations" },
          { "field": "sum_citations_CrossRef", "type": "quantitative", "title": "Sum CrossRef Citations" },
          { "field": "mean_citations_Aminer", "type": "quantitative", "title": "Mean Aminer Citations" }
        ]
      }
    }
  ]
}

